css:
@-moz-document url-prefix("https://www.vlr.gg") {
/* HEADER */

.header-search input {
    border-radius: 5px;
}

}

@-moz-document url-prefix("https://www.vlr.gg/threads"), url-prefix("https://www.vlr.gg/forums") {
/* FORUMS */

a.wf-nav-item, .wf-nav-item {
    border: 1px solid #5b6167;
    border-radius: 5px;
    margin-right: 20px;
    margin-top: 5px;
    margin-bottom: 5px;
}

a.wf-nav-item.mod-active, .wf-nav-item.mod-active {
    box-shadow: inset 100px 100px #00000077;
    cursor: pointer!important;
}

.wf-nav-item.mod-active:before {
    display: none;
}

.wf-nav-item.mod-active:after {
    display: none;
}

.wf-nav {
    background-color: #2f3337;
}

div.threads-filter {
    background-color: #394046;
    border-radius: 5px;
    box-shadow: 0 1px 3px -1px rgba(0, 0, 0, 0.4);
}

.btn, a.btn {
    border-radius: 5px;
    box-shadow: none;
}

.btn:before, a.btn:before {
    display:none
}

.btn.is-clicked {
    box-shadow: inset 100px 100px #00000077, 0 0 20px 5px #00000077!important;
}

.wf-dropdown:before {
    display:none
}

.wf-dropdown {
    background: #394046;
    box-shadow: inset 100px 100px #00000077, 0 0 20px 5px #00000077;
    border-radius: 5px;
}

.wf-dropdown.mod-all a {
    border-radius: 5px;
    margin: 5px;
    padding: 10px;
}

#search-bar {
    border-radius: 5px;
}

.btn.mod-action {
    border-radius: 5px;
}

.wf-card:before {
    display:none
}

#thread-list {
    border-radius: 5px;
}

.forum-container {
    border-radius: 5px;
}
}
