@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("aarinfantasy.com") {

/**font size for text in shoutbox. Comment out or remove if text in the shoutbox was fine before this style was applied**/
#shoutbox_frame .smallfont {font-size: 10.3px !important;}

body, .page, .alt1 .smallfont div, .alt1, .alt1Active, .tfoot, .vb_menu_control, .panel, .panelsurround, .vb_postbit_info, .announcement, .smallfont .spacer, .border_in {background: #5a6272 !important; }

/**"You must be 16 years and older..." text, some red text that I saw in the shoutbox, and some announcement text**/
.panel font, #shoutbox_frame font, .panel b font {color: #770E0F !important;}



.poll {background: #655A72 !important;}

.vb_postbit_info {background: #686E79 !important;}

.vbmenu_popup table {background: #283a5e !important; border: 1px solid #283a5e !important;}
.vbmenu_option { background: #5a6272 !important;} 

.alt1Active {border-left: 0px !important; border-top: 0px !important;}

.alt2, .alt2Active { background: #686e79 !important; border-top: #686e79 !important; border-left: #686e79 !important; border-right: #686e79 !important; }

.tborder { background: #a5a6a7 !important; border: 1px solid #a5a6a7 !important; } 

.tcat { border-top: #4e607a 1px solid !important; border-left: #4e607a 1px solid !important; border-right: #4e607a 1px solid !important; } 

.tcat .smallfont, .tcat, .thead, .vb_menu_control, .vbmenu_control, .vb_footer { background: #283a5e !important; }

.foot { border-top: #ffffff 1px solid !important; border-left: #ffffff 1px solid !important; border-right: #ffffff 1px solid !important; border-bottom: #ffffff 1px solid !important; }

.alt1, alt1Active { border-left: 1px solid #3d4a5e !important; border-top: 1px solid #3d4a5e !important; }

.tcat, .thead, .navbar, .highlight, .vbmenu_control, .pre-spoiler, .vb_menu_control, .vb_footer { font-weight: bold !important; }

.sticky { background-color: #525967 !important; color: #1f293e !important; border-left: 1px solid #525967 !important; border-top: 1px solid #525967 !important; } 

.poll { border-left: 1px solid #5a6272 !important; border-top: 1px solid #5a6272; } 

.vbmenu_control { border-top: #283a5e 1px solid !important; border-right: #283a5e 1px solid !important; }

.tfoot { border-top: #5a6272 1px solid !important; border-left: #5a6272 1px solid !important; border-right: #5a6272 1px solid !important; border-bottom: #5a6272 1px solid !important; } 

td.inlinemod { border-left: 1px solid #5a6272 !important; border-top: 1px solid #5a6272 !important; } 

.announcement { border-left: 1px solid #5a6272 !important; border-top: 1px solid #5a6272 !important; } 

.moderated { border-left: 1px solid #5a6272; border-top: 1px solid #5a6272 !important; }

td.vBulletin_editor#vB_Editor_QR , .controlbar {background: #5A6272 !important;}

.vb_postbit {color: #03002F !important;}

.poll {border-top: 0px !important;}

.vbmenu_hilite {background: #8F8F90 !important;}

b font, font b {color: #000 !important;}

.navbar strong, .alt1 .smallfont, .time, .alt2 .smallfont, .smallfont a, .smallfont, .alt1 a strong, .alt1 a, .alt1, pre.alt2 div, .smallfont div a strong, .poll a, .poll div, .fieldset legend, .fieldset a, .vbmenu_popup a, .alt1Active strong, #threadslist a, .announcement strong, .announcement .smallfont strong, .alt1 .smallfont strong, .alt2 .smallfont strong, .panel b a, a:hover, a:active, body_ahover, .panel div span a, .page .smallfont strong , .vb_postbit_info, .panel ul li a {color: #1f293e !important;}

.smallfont strong a, .smallfont strong, .vbmenu_control, #forumtools a, #forumsearch a, .vbmenu_popup .thead a, .tcat b, .tcat a, #threadslist tbody .thead a, #threadtools a, #threadsearch a, #threadrating_current, .tcat .normal, .tcat {color: #fff !important;}

}
