css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("teuxdeux.com") {

#container {
    width: 980px !important;
}
.daily_list {
    padding-top: 32px !important;
}
ul.list_items {
    width: 163px !important;
    min-height: 204px !important;
    height: 204px !important;
}
#someday_list {
    height: 212px !important;
}
#content_wrapper,
#someday_list {
    width: 905px !important;
}
#header,
#deux_list,
#someday,
#someday .header {
    width: 915px !important;
}
#container a.left {
    top: 63px !important;
    left: 1px !important;
}
#container a.right {
    top: 63px !important;
    right: 1px !important;
}
.five,
.daily_list_wrapper,
.daily_list_wrapper h3,
.daily_list_wrapper h4,
.overflow_abs {
    width: 163px !important;
}
ul.list_items .overflow p.teuxdeux {
    width: 149px !important;
}
.daily_list input[type="text"] {
    width: 156px !important;
}

#someday .header h2 {
    margin-top: 7px !important;
}
#someday .header input,
.daily_list input[type="text"] {
    background-color: #FCFCFC !important;
    border-top: 1px solid #DDD !important;
    border-left: 1px solid #DDD !important;
}
#someday_list .list_items {
    margin-right: 20px !important;
    padding-right: 0 !important;
    background-repeat: repeat-x !important;
    border: none !important;
}
#someday_list .list_items.last {
    margin-right: 0 !important;
}

ul.list_items .overfloweth p.teuxdeux {
  /* Thanks to krewetka for this rule! */
    width: 163px !important;
}

}
