css:
@-moz-document domain("www.sudomemo.net"), domain("sudomemo.net") {
body
  {
    background-color: #000;
    color: #000;
  }

  .theme-body
  {
    background-image: url(https://cdn.discordapp.com/attachments/928354505152671744/1031146620714229870/anigif.gif);
    background-attachment: scroll;
  }

    body {
    background-color: #000;
    color: #fff;
}
  
   .panel-body
  {
    background: #111 !important;
    color: #81c106;
  }

  .theme-panel-body
  {
    background-color: #fff0 !important;
  }

  .panel-footer
  {
    background: #222 !important;
  }

  .flipnote-description
  {
    background: #263a00 !important;
    color: #81c106 !important;
  }

  :root
  {
    --theatre-primary: #81c106;
  }

  .name
  {
    color: #999 !important;
  }

  .about-sudomemo
  {
    background-color: #3b5a00 !important;
  }

  .about-sudomemo p
  {
    background-color: #3b5a00;
  }

  .about-sudomemo a span
  {
    background-color: #263a00 !important;
  }

  .panel-heading
  {
    background-color: #1b661b;
    color: #fff !important;
  }

  a
  {
    color: #ffffff;
  }

  div#flipnote-stats>ul>li:nth-child(4)
  {
    color: #fff;
  }

  div#flipnote-stats>ul>li:nth-child(6)
  {
    color: #fff;
  }

  div#flipnote-stats>ul>li:nth-child(3)
  {
    color: #fff;
  }

  div#flipnote-stats>ul>li:nth-child(2)
  {
    color: #fff;
  }

  div#flipnote-stats>ul>li:nth-child(1)
  {
    color: #ffffff;
  }

  .name
  {
    color: #fff !important;
  }

  .comment
  {
    background-color: #263a00 !important;
  }

  .theme-link
  {
    color: #81c106;
  }

  .panel-common
  {
    background-color: #3b5a00;
  }

  ul.flipnote-list>li.flipnote-list-item
  {
    background-color: #3b5a00;
  }

  .box-container
  {
    background-color: #fff !important;
  }
  .player__controls
  {
    background-color: #263a00;
  }

  .info-buttons input
  {
    background-color: #555;
  }

  .btn-custom
  {
    background-color: #649505 !important;
    color: #fff;
    border: none;
  }

  div.follow-button>div.btn-group>button.btn-custom
  {
    color: #ffffff;
  }

  .btn-custom:hover
  {
    background-color: #263a00 !important;
    color: white;
    border: none;
  }

  .btn-custom-active
  {
    background-color: #263a00;
    border: none;
  }

  .btn-custom-active:hover
  {
    background-color: #188418;
    color: #fff;
    border: none;
  }

  a
  {
    color: #fff;
  }
    
  .star-country-index
  {
    color: white;
  }

  .navbar-custom:after
  {
    background-color: #444 !important;
  }

  .player__scrubber
  {
    background-color: #4b861c;
  }

  h1, h2, h3, h4, h5, h6
  {
    color: #81c106;
    font-weight: 600;
    line-height: 1.3em;
  }
}
