css:
@-moz-document domain("www.nitrotype.com")
{
body {
    background: none;  
}
html {
    background: fixed url(https://w.wallha.com/ws/12/gquz0df7.jpg), #001d45;
    background-size: 100%
}
.card--grit {
    background: none;
}
.bg--gradient {
    background: fixed;
}
.theme--pDefault .profile-bg
{
    background: url("https://c4.wallpaperflare.com/wallpaper/108/140/869/digital-digital-art-artwork-fantasy-art-drawing-hd-wallpaper-preview.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffff 100%);
    color: #d3d3d3;
    background-size: 107.5%
}

.theme--pDefault .profile-bgHead
{
   background: url("https://c4.wallpaperflare.com/wallpaper/108/140/869/digital-digital-art-artwork-fantasy-art-drawing-hd-wallpaper-preview.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffff 100%);
    color: #d3d3d3;
    background-size: 107.5%
}

.theme--pDefault.is-gold .profile-bg
{
    background: url("https://c4.wallpaperflare.com/wallpaper/108/140/869/digital-digital-art-artwork-fantasy-art-drawing-hd-wallpaper-preview.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffff 100%);
    color: #d3d3d3;
    background-size: 107.5%
}
.card--a
{
    background-color: #fff0;
    color: #d000ff;
}
.dash-copyContainer {
    background: rgba(0, 0, 0, .78);
    box-shadow: 0 1px 157px 10px rgba(0, 0, 0, 0.78);
}
.dash-copy {
    color: #d000ff;
    font-size: 32px
}
.growl--a {
    background-image: url("https://c4.wallpaperflare.com/wallpaper/184/515/626/digital-digital-art-artwork-illustration-drawing-hd-wallpaper-preview.jpg");
    border-color: rgba(2, 2, 2, 0.2);
    background-size: 100%;
    color: #f700ff;
}
.with-team-ranking .profile--grid--player-info .type-ellip, .with-racer-ranking ,.profile--grid--player-info
.type-ellip {
    color: #d000ff      
}
.theme--pDefault .profile-username {
    color: #ff6f00
}
.profile-carContainer.card.card--a.card--shadow-l {background-color:#fff0;}
.raceResults-awards {background:#fff0;}
.raceResults-close.raceResults-close--minimizer {background:#f700ff;}
.g.raceResults-grid {background:#fff0;}
.dash-content {background:url("https://c4.wallpaperflare.com/wallpaper/108/140/869/digital-digital-art-artwork-fantasy-art-drawing-hd-wallpaper-preview.jpg");
    background-size: 100%
}
.raceResults--default 
{background-image: url("https://w.wallha.com/ws/12/gquz0df7.jpg");
          background-size: 100% 
}
}
