css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("google.com") {
  /* Displays position-numbers in SERPs
     (c) 2011 by Lestarte
     Last Update: 19.10.2011 */

  object[data*="content"],

  ol {
    counter-reset: pageNum;
  }

  div[class=vsc]:after {
    content: counter(pageNum) ": ";
    counter-increment: pageNum;
    font-weight: bold;
    font-size:16px;
    color:#2200C1;
    position:absolute;
    top:0px;
    left:-25px;
  }
}
