css:
@-moz-document url-prefix("https://discord.com")
{
/* ------BACKGROUND------ */
  body
  {
    background: url "https://userstyles.org/style_screenshots/238992_after.jpeg?r=1674547305" !important;
  }

  .appMount-3lHmkl
  {
    background: "https://wallpaperaccess.com/full/568034.jpg";
  }

/* ------REMOVE GREY/WHITE BG COLORS------ */
  .theme-dark,
.theme-light
  {
    --background-primary: "https://wallpaperaccess.com/full/568034.jpg";
    --background-secondary: "https://wallpaperaccess.com/full/568034.jpg";
    --background-secondary-alt: "https://wallpaperaccess.com/full/568034.jpg";
    --background-tertiary: "https://wallpaperaccess.com/full/568034.jpg";
    --background-floating: "https://wallpaperaccess.com/full/568034.jpg";
    --channeltextarea-background: "https://wallpaperaccess.com/full/568034.jpg";
    --deprecated-panel-background: "https://wallpaperaccess.com/full/568034.jpg";
    --background-modifier-hover: "https://wallpaperaccess.com/full/568034.jpg";
    --background-modifier-selected: "https://wallpaperaccess.com/full/568034.jpg";
  }

/* ------POP-UP------ */
  .modal-yWgWj-,
.footer-3rDWdC
  {
    background: "https://i.pinimg.com/564x/36/fd/74/36fd7465c6efd2cc221f46ff62ef7ad3.jpg" !important;
  }


/*remove borders*/
  .header-2o-2hj,
.searchBar-6Kv8R2,
.content-yTz4x3::before
  {
    box-shadow: none !important;
  }

/*misc*/
  .container-1D34oG,  /*friends list*/
.scrollbar-2rkZSL,  /*scrollbar*/
.pad-29zQak,
.track-1JN30G
  {
    background: "https://i.pinimg.com/564x/36/fd/74/36fd7465c6efd2cc221f46ff62ef7ad3.jpg" !important;
  }

/* ------SCROLLBAR------ */
  .scrollbar-2rkZSL
  {
    width: 8px;
  }

  .thumb-2JwNFC
  {
    background: "https://i.pinimg.com/564x/36/fd/74/36fd7465c6efd2cc221f46ff62ef7ad3.jpg" !important;
  }

  .track-1JN30G,
.thumb-2JwNFC
  {
    border: none !important;
  }

/* ------EMBEDDED TEXT------ */
  .embedProvider-3k5pfl,
.embedDescription-1Cuq9a,
.embedFields-2IPs5Z,
.embedAuthorName-3mnTWj,
.embedFooterText-28V_Wb
  {
    color: #f2f2f2 !important;
  }

  .cozy-3raOZG .messageContent-2qWWxC, .compact-T3H92H .header-23xsNx .username-1A8OIy, .cozy-3raOZG .header-23xsNx .username-1A8OIy
  {
    text-shadow: 0 0 20px black;
  }
}
