css:
/* i really want this to be global */
 body, html {
    background: #262626 !important;
}
#main {
    background-color: #262626 !important;
}
* {
    background-color: #e0e0e000 !important;
    border: 0px !important;
}
body, #base, #main {
    height:100% !important;
}
#header, #footer-holder, .subject, .location, .cams, ux, .thumbnail_label, a.tabLink, div.tabLink, .BioContents, .fanclubButton, #satisfactionScore, .draggableCanvasChatWindow, #FollowSettingsModal, .dismissibleMessage {
    display:none !important;
}
#VideoPanel > div:nth-child(4) {
    height: 900px !important;
}
div[ts="Bx"], div[ts="QT"] {
    height: auto !important;
}
/************************
	fix slider color
************************/
 .slider > div:nth-child(1) {
    background-color: black !important;
}
.slider > div:nth-child(2) {
    background-color: white !important;
}
.slider > div:nth-child(3) {
    background-color: gray !important;
}
/************************
	follow container
************************/
 .followedContainer {
    background-color: #262626 !important;
    height: 800px;
    width: 800px !important;
    max-height: 800px !important;
}
.followedContainer > div:nth-child(2), .followedContainer > div:nth-child(2)> div, .followedContainer > div:nth-child(2)> div> div {
    display: contents !important
}
/************************
	remove chat
************************/
 .roomSubject, .reportAbuseLink, #ChatTabContainer, .theatermodePureChat {
    display:none !important;
}
.styledDiv, .RoomTabs {
    position: absolute !important;
    display: initial !important;
    top: 0 !important;
    right: 20% !important;
}
#roomTabs {
    position: absolute !important;
    top: 0 !important;
    left: 50% !important;
}
.topSectionWrapper {
    display:contents !important;
}
.title {
    border-bottom: 0px !important;
}
#VideoPanel > div:nth-child(5) {
    display:none !important
}
.BaseRoomContents, .topSectionWrapper, #VideoPanel {
    background-color: #e0e0e000 !important;
    border: 0px !important;
    padding: 0 !important;
    margin: 0 !important;
}
.videoPlayerDiv {
    height: inherit !important;
    width: auto !important;
}
.top-section, .gender-tab, .tabActiveColor {
    font-size: 10px !important;
    border: 0px !important;
    padding: 0 0 0 10px !important;
    margin: 2px !important;
    height: fit-content !important;
}
.sub-nav {
    background-color: #262626 !important;
}
.list li {
    background-color: #262626 !important;
    border-color:#000000 !important;
    color: #999999 !important;
    width: 19% !important;
    max-height: 100% !important;
}
.list li img {
    width:100% !important;
    height:100% !important;
}
#roomTabs {
    min-height:0!important;
    width: fit-content !important;
    float:right;
}
/************************
	COLOR
************************/
 .logo-zone {
    background-color: #262626 !important;
}
.top-section {
    background-color: #262626!important;
    background: #262626 !important;
}
#header #user_information {
    background: #262626 !important;
    border-color: #000000!important;
}
.user_information {
    color: #000000 !important;
}
.user_information_header, #VideoPanel {
    background-color: #262626;
    background: #262626 !important;
}
.room_list, #main .genderTabs .tabAnchor.orange {
    background-color: #262626 !important;
}
#header .section {
    background-color: #262626 !important;
}
.footer-holder {
    background: #262626 url(https://ssl-ccstatic.highwebmedia.com/images/bg-footer-holder.gif?cc1e09ef1e7a) repeat-x;
}
.footercon {
    background: #262626 !important;
}
#hu.bio-container {
    background: #262626 !important;
    border-color: #262626 !important;
    color: #ffffff !important;
}
.bottom {
    color:#ffffff !important;
    background:#262626 !important;
}
.tokencountlink {
    color:#ffffff !important;
}
#main > div, #main.chat_room, #footer-holder.footer-holder {
    background: #262626 !important;
    border-color: #262626 !important;
}
