css:
@-moz-document domain(www.myproana.com)
{


html, body, p, a, h1, h2, h3, h4 {
    font-family: 'Inter', sans-serif;
}

#header_bar {
    background: #323232;
}

#branding {
    border-bottom: none;
    padding: 30px 0;
}

#branding, #primary_nav {
    background: #6e4b88;
}

#community_app_menu>li.active>a {
    color: #131313;
    background:#fff;
    font-weight: bold;
}


#community_app_menu>li>a {
    color: #131313;
    background: #ffffffe3;
    text-shadow: none;
}


html, body {
    background-color:#e9ecf0;
}


table.ipb_table, .ipsBox, .ipsBox_container  {
    border: none;
}

a {
    color: #354e60;
    text-decoration: none;
}


#content {
    background: #fff;
    box-shadow: none;
}

#board_index {
    background: #fff;
}

.ipsBox, .unread {
    background: #fff;
}


.maintitle {
    background: #DBE2EC;
    box-shadow: none;
    border: none;
}

.maintitle a{
    color: #354e60;
    font-weight: 600;
    letter-spacing: 1px;
}

.maintitle {
    margin: 10px 5px;
}

.ipsLayout_right .clearfix{
    margin-top: 10px;
    margin-right: 5px;
}



#nav_explore, #primary_nav #quickNavLaunch, #search_options, #adv_search, #tickerRow .icon, #tickerRow .data-count, .col_c_post .ipsUserPhotoLink, .__xXtoday27s20birthdays {display:none}


#search_wrap {
    position: relative;
    background: #ffffff1c;
    display: block;
    padding: 5px 10px;
    height: 26px;
    line-height: 25px;
    box-shadow:none;
}

#main_search::placeholder, #main_search {
  color: #fff;
  background: none;
}

#search .submit_input {
    background: rgba(255, 127, 127, 0) url(https://www.myproana.com/public/style_images/master/search_icon.png) no-repeat 50%;
    border:none;
    width: 26px;
    height: 100%;
    border-radius: 0;
    box-shadow: none;
}

.category_block .ipb_table h4 {
    font-size: 14px;
    word-wrap: break-word;
}

.ipsBadge_green, .pagination .pages li.active {
    background: #d28656;
}

.ipsBadge_orange {
    background: #ff2946;
}

#activity-ticker li {
    background: white;
}

#tickerRow .post-title {
    font-size: 15px;
}

.last_post {
    margin-left: 10px;
}


blockquote.ipsBlockquote, p.citation {
    background: #dbe2ec78;
}

blockquote.ipsBlockquote {
    padding-top:0;
}

p.citation {
    padding: 0 20px;
    padding-top:20px;
    border: 1px solid #848a8c30;
    border-bottom:none;
    border-radius: 5px 5px 0px 0px;
}

blockquote.ipsBlockquote {
    border: 1px solid #848a8c30;
    border-top:none;
    border-radius: 0px 0px 5px 5px;
    padding: 20px;
    margin-bottom: 15px;
}

.ipsUserPhoto, .ipsUserPhotoLink:hover .ipsUserPhoto {
    border: none!important;
    box-shadow: none;
}

.post_body .post {
    padding:20px;
    font-size: 14px;    
}

.posted_info {
    padding-left: 20px;
}


.ipsUserPhoto_large {
    max-width: 100px;
    max-height: 100px;
}

.author_info .group_title {
    margin-top: 20px;
}

.custom_fields {
    font-size: 10px;
}

.ipsLikeButton {
    padding: 5px 10px;
    font-size: 11px;
    font-weight: 600;
    color: #fff !important;
}

.ipsFilterbar li a {
    text-shadow: none;
}

.ipsLikeButton.ipsLikeButton_enabled {
    background: #6e4b88;
    border: none;
}

.ipsButton_secondary {
    background: #f6f6f6;
    border: none;;
    border-radius: 3px;
    color: #616161;
}
.maintitle, .ipsFilterbar li a {
        color: #616161;
}

div.bbc_spoiler_wrapper {
    border: none;
}

}
