css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("renren.com") {
div[id="nostar"] {
        display:none!important
        }
div[id="paintself"] {
        display:none!important
}
div[class="profile-film box"] {
        display:none!important
}
#tobestar,.blank-holder,.ad-bar,.side-item.selected,.side-item.sales-poll,.home-body #info, .xn2rr {
		display: none !important
}
.beta-bar{
	display: none !important;
}
/*new navigation*/
.menu-bar{padding-bottom: 0px !important;
	background: url(http://xnimg.cn/img/zidou/nav-bg.png) repeat-x left top;border-bottom:1px solid #ccc !important; height:31px !important; overflow:hidden !important;
}

div[id^="logo"] h1 a {
		display: block !important;
		margin-top: 8px !important;
		background: url(data:image/png;base64,) no-repeat center center !important;
		height: 20px !important;
		width: 101px !important;
	}
	div[id^="logo"] h1 a img {
		display: none !important;
	}
#navBar .navigation, .navigation .nav-main, .navigation .nav-body,.kx .navigation .nav-other{background:transparent !important;margin-left:0 !important;}
.navigation .banner{ border: none !important;}
.navigation .menu {line-height:31px !important;}

.navigation .menu-title{font-size: 12px !important;}
.navigation .nav-other {background-image:none !important;}
.navigation .nav-main .menu-title a, .navigation #searchMenu .menu-title a, .navigation .nav-other .menu-title a {color: #005eac !important;}
.navigation .nav-other .menu .charge a{background-position:4px -85px !important}
.navigation .nav-main .menu-title a:hover, .navigation .menu-title a:hover{background-color:transparent !important;  text-decoration: underline;color:#005eac !important}
#navSearch {margin:6px 5px 0 !important;}
#logo h1 a{margin-top:1px !important}
#logo2 h1 a{margin-top:1px !important;display:block !important;}
.nav-shadow{border:none !important}
.navigation .blank-holder{border:none !important}
.nav-shadow {border-bottom: none !important;}
.navigation .menu-title a .count {color:#666 !important;}
.profile .navigation .nav-other .menu .charge a:hover{background-position:4px -85px !important;background-color:transparent !important}

/*search a is white*/
.navigation .nav-main .menu-title a.searchcolor{color:#005eac !important;}
.navigation .menu-title.with-arrow a{background:url(http://xnimg.cn/imgpro/bg/cs_navtop.gif?ver=090820) 40px -140px no-repeat !important;}

.kx .navigation {background-color:transparent !important;}
}
