css:
@-moz-document domain("onlinegdb.com"){
 
:root{
	
  	--mwhite:#f6f6f6;
  	--mpink:#f92772;
  	--mblue:#66d9ee;
  	--morange:#fe9720;
  	--mpurple:#ae81ff;
  	--myellow:#e6da74;
  	--mgreen:#a6e22e;
  	--mblack:#1c1c1c;
  	--mgrey: #323232;
  
}
 
.ace-idle-fingers{
  	color: var(--mwhite);
  	background: var(--mblack);
}

.ace_editor{
	font-family: monospace;
}

.ace-idle-fingers .ace_keyword, .ace-idle-fingers .ace_meta{
	color: var(--mpink);
}

.ace-idle-fingers .ace_constant, .ace-idle-fingers .ace_constant.ace_character, .ace-idle-fingers .ace_constant.ace_character.ace_escape, .ace-idle-fingers .ace_constant.ace_other, .ace-idle-fingers .ace_support.ace_constant{
	color: var(--mpurple);
}

.ace_paren, .ace_rparen{
	color: var(--mwhite);
}

.ace_identifier{
	color: var(--mblue);
}

.ace-idle-fingers .ace_keyword, .ace-idle-fingers .ace_meta{
	color: var(--mpink);
}

.ace-idle-fingers .ace_string{
	color: var(--myellow);
}

.ace-idle-fingers .ace_support.ace_function{
	color: var(--mpink);
}
  
span.ace_support.ace_type{
  	color: var(--mblue);
}
  
.ace_line #text{
	color: var(--mgreen);
}

.ace_gutter{
	background: #101010;
  	color: #808080;
}

#multi_editor_container{
	background: black;
}

#control-bar .btn-half{
	background: #101010;
}

.glyphicon-cloud-upload:before{
	color: white;
}

.glyphicon-file:before{
	color: white;
}

#control-bar button{
	border-radius: 5px;
  	color: #101010;
}

#control-bar button{
	filter: brightness(0.8);
}

.btn-success{
	background: #0cd070;
}

.btn-success:hover{
	background: #0cd070;
	filter: brightness(0.8);
}

.btn-primary{
	background: #00d8ff;
}

.btn-primary:hover{
	background: #00d8ff;
	filter: brightness(0.8);
}

.btn-danger{
	background: #f92772;
}

.btn-danger:hover{
	background: #f92772;
	filter: brightness(0.8);
}

.btn-warning{
	background: #fe9720;
}

.btn-warning:hover{
	background: #fe9772;
	filter: brightness(0.8);
}

.btn-info{
	background: #8e27e0;
}

.btn-info:hover{
	background: #8e27e0;
	filter: brightness(0.8);
}

.form-control{
	background: #101010;
  	border: white solid 2px;
  	color: white;
}

}
