css:
@-moz-document domain("youtube.com") {
/* --------- YT TWIX --------- */
/* ----------- CML ----------- */


/* - COMPACT - */
/* home */
#contents.ytd-shelf-renderer { margin-left: 30px; }
ytd-grid-video-renderer { margin-right: 50px; }
ytd-rich-grid-renderer { margin-left: 40px; margin-top: 25px; }
ytd-rich-item-renderer { width: 215px; margin-right: 35px; }

/* home item */
#avatar-link.ytd-rich-grid-media { height: 30px; margin-right: 5px; margin-top: 15px; }
#avatar.ytd-rich-grid-media { width: 30px; height: 30px; }
#meta.ytd-rich-grid-media { padding-right: 0; }
ytd-video-meta-block[rich-meta][mini-mode] #metadata-line.ytd-video-meta-block { font-size: 1.1rem; }
ytd-toggle-button-renderer.style-compact-gray[is-paper-button] tp-yt-paper-button.ytd-toggle-button-renderer { padding: 5px; }

/* home thumb */
ytd-video-renderer[use-prominent-thumbs] ytd-thumbnail.ytd-video-renderer, ytd-playlist-renderer[use-prominent-thumbs] ytd-playlist-thumbnail.ytd-playlist-renderer { max-width: 220px; min-width: 50px; }
ytd-video-renderer[use-prominent-thumbs] .text-wrapper.ytd-video-renderer { max-width: 600px; }

/* meta */
#call-to-action.ytd-rich-metadata-renderer { margin-top: 0; font-size: 1.2rem; line-height: 1.2rem; font-weight: 400; }
yt-icon.ytd-rich-metadata-renderer { width: 18px; height: 18px; }

/* search results */
ytd-video-renderer[is-search] ytd-thumbnail.ytd-video-renderer, 
ytd-playlist-renderer[is-search] ytd-playlist-thumbnail.ytd-playlist-renderer, 
ytd-channel-renderer[use-prominent-thumbs] #avatar-section.ytd-channel-renderer { max-width: 240px; }
ytd-video-renderer[is-search] #channel-info.ytd-video-renderer { padding: 2px 0; }
.metadata-snippet-container-one-line.ytd-video-renderer { margin-bottom: 4px; }
#expandable-metadata.ytd-video-renderer:not(:empty) { margin: 2px 0; }
#badges.ytd-video-renderer { margin-bottom: 0;  display: none; }
yt-img-shadow.ytd-video-renderer { max-height: 20px; max-width: 20px; }
ytd-shelf-renderer.ytd-item-section-renderer #contents.ytd-shelf-renderer { margin-left: 0px !important; } 
ytd-video-renderer[is-search] #video-title.ytd-video-renderer, 
ytd-two-column-search-results-renderer #video-title.ytd-playlist-renderer { font-size: 1.5rem; line-height: 1.75rem; }
#collapse-content.ytd-search-sub-menu-renderer { margin-bottom: 20px; margin-top: 10px; }
#filter-group-name.ytd-search-filter-group-renderer { margin: 0; padding: 0; }
ytd-search-filter-renderer.ytd-search-filter-group-renderer { padding: 5px 0 0; }

/* sub feed */
ytd-two-column-browse-results-renderer.grid:not(.grid-disabled) { margin-left: 30px; }
#items.ytd-grid-renderer > ytd-grid-video-renderer.ytd-grid-renderer { margin-right: 50px; margin-bottom: 40px; }
#spacer.ytd-shelf-renderer { flex: 0.915; }

/* discover */
#destination-content-root.ytd-destination-button-renderer { flex-direction: row; }
#destination-buttons.ytd-destination-shelf-renderer { margin-top: 20px; margin-bottom: 0; margin-right: 0; }
ytd-destination-button-renderer { width: 205px; }
#destination-icon.ytd-destination-button-renderer { margin: 5px 5px 5px 10px; }
#destination-label-wrapper.ytd-destination-button-renderer { margin: 0px 5px 5px 10px; height: 40px; max-height: 40px; }
yt-horizontal-list-renderer { max-width: 96%; }
yt-horizontal-list-renderer ytd-grid-video-renderer { margin-right: 57px; }
#avatar.ytd-shelf-renderer { height: 20px; width: 20px; }


/* ------------------------------------------------------ */


/* - REMOVAL - */
/* covid etc notes */ #clarify-box { display: none; }
/* filter bar */ ytd-feed-filter-chip-bar-renderer, #chips.yt-chip-cloud-renderer { display: none; }
.ytp-ce-bottom-right-quad { display: none; }
/* result sidebar */ #secondary.ytd-two-column-search-results-renderer { display: none; }


/* ------------------------------------------------------ */


/* - VIDEO - */
h1.ytd-watch-metadata { font-family: "Roboto","Arial",sans-serif; font-weight: 400; }
#super-title.ytd-watch-metadata { font-size: 1.2rem; font-weight: 400; line-height: 2rem; }
#super-title.ytd-watch-metadata { background: rgba(128,128,128,0.15); padding: 2px 4px; border-radius: 5px; }
yt-formatted-string.ytd-text-inline-expander:nth-child(1) > span:nth-child(4) { display: block; }
#formatted-snippet-text *:nth-child(n+4) { display: block; }
#comment-teaser { visibility: hidden; }
ytd-watch-metadata:not([modern-metapanel]) #owner.ytd-watch-metadata, 
ytd-comments-entry-point-header-renderer:not([modern-metapanel]) { border: none; padding: 0; }
ytd-watch-metadata:not([flex-menu-enabled]) #description-and-actions.ytd-watch-metadata { flex-direction: column-reverse; }
ytd-watch-metadata[top-aligned-actions]:not([actions-on-separate-line]) #actions.ytd-watch-metadata { justify-content: flex-start; }
#expand.ytd-text-inline-expander, .button.ytd-text-inline-expander { background: rgba(128,128,128,0.15); }
#expand.ytd-text-inline-expander:hover, .button.ytd-text-inline-expander:hover { background: rgba(96,96,96,0.15); }
ytd-watch-metadata[top-aligned-actions] #actions.ytd-watch-metadata, #description-and-actions #description, 
#description-and-actions.ytd-watch-metadata { margin-top: 0; }
#channel-name.ytd-video-owner-renderer { font-size: 1.25rem; line-height: 1.5rem; }
ytd-video-owner-renderer[watch-metadata-refresh] #owner-sub-count.ytd-video-owner-renderer { font-size: 1.1rem; line-height: 1.6rem; }
ytd-watch-metadata:not([modern-metapanel]) #owner.ytd-watch-metadata, 
ytd-watch-metadata:not([modern-metapanel]) #comment-teaser.ytd-watch-metadata { height: auto; }
ytd-watch-metadata:not([actions-on-separate-line]) #actions.ytd-watch-metadata { justify-content: flex-start; }
/*
ytd-video-owner-renderer[watch-metadata-refresh]:not([modern-metapanel]) #avatar.ytd-video-owner-renderer { width: 30px; height: 30px; }
tp-yt-paper-button.ytd-subscribe-button-renderer { border-radius: 2px; padding: 5px; font-size: 1.3rem; }
*/
/*
#description-inline-expander { display: flex; flex-direction: column-reverse; }
#above-the-fold { display: flex; flex-direction: column; }
#above-the-fold #title { order: 1; }
#above-the-fold #owner-and-teaser { order: 2; }
#above-the-fold #description-and-actions { order: 3; }
.item.ytd-watch-metadata { margin-top: 5px; }
*/

    
}
