css:
@-moz-document domain("2chan.net"), domain("tsumanne.net"), regexp("^https?://.*ftbucket.+"), regexp("^https?://(board|kako)\\.futakuro\\.com/.*") {
/* ---------- ふたば全域 ---------- */

body, td.rtd, td.ftdc {
  color: #ccc;
}
body {
  background: #333;
}
a:link, a:visited {
  color: #7bbfd8 !important;
}
a:hover {
  color: #a4d7ea !important;
}

/* 各種入力欄とプルダウンメニューの色をページと揃える */
input:not(.zoom_button):not(.download_button):not(.check_button):not(.inline_button):not(.zbtn), select, textarea, .ex_button {
  background-color: #333 !important;
  border-color: #aaa !important;
  color: #c5c5c5 !important;
}

/* ふたクロの左右ラベル */
#left_label, #right_label {
  background-color: #333;
}
/* 「続きを読む」「再読込」ボタンを少し小さく */
#fvw_loading{
  padding-left: 20px;
  padding-right: 20px;
}
/* ふたクロツールバーのポップアップ全般 */
.window {
  background-color: #444;
}
.drag_handle {
  background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#333), to(#444));
}

/* ツールバーのhover色 */
.f_menu:hover, .f_menu_dot_left:hover, .f_menu_dot_right:hover {
  background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#fff), to(#cde));
}
/* ツールバーのリンクを元の色に戻す */
#fvw_menu a.noborder {
  color: #333 !important;
}
/* 「ふたクロについて」のリンクの色 */
#info_window a {
  color: #c5c5c5;
}
/* QuickJump */
/*
#quick_viewer {
  background: rgb(74, 74, 74);
}
.quick_block:hover {
  background-color: #666;
}
*/
#quick_viewer::-webkit-scrollbar-thumb {
    background-color: rgb(128, 128, 128);
}
#quick_close {
  border-top-color: #333;
  border-left-color: #333;
  border-bottom-color: #333;
}

/* mht保存とかのポップアップ */
#alert_window {
  background: #333;
  border-color: #333;
}

/* 赤福 */
#akahuku_sagebutton {
  color: #7bbfd8 !important;
}
.akahuku_saveimage_button {
  color: #7bbfd8 !important;
}

}

@-moz-document regexp("^https?://\\w+\\.2chan\\.net/b/futaba\\.htm$") {
/* ---------- じゃない方 ---------- */

/* レス省略数表示 */
.maxres + font[color='#707070'] {
  color: #cca;
}

/* レスNo */
.cno:hover {
  color: #a4d7ea;
}

/* 引用ポップアップ */
.qtd {
  background-color: #4a4a4a;
  box-shadow: 1px 1px 3px 1px #aaa;
  color: #ccc;
}
.qtjmp {
  color: #7bbfd8 !important;
}
.qtjmp:hover, .qsd:hover {
  color: #a4d7ea !important;
}

}

@-moz-document regexp("^https?://\\w+\\.2chan\\.net/\\w+/futaba\\.php\\?mode=cat.*$") {
/* ---------- カタログ ---------- */

table[border='1'] {
  border:1px solid #bbb;
}
table[border='1'] td {
  border:1px double #888;
}

/* スレ枠右下のメニューボタン */
.pdmc {
  background-image: url("data:image/svg+xml;base64,") !important;
  opacity: 0.8;
}
.pdmc:hover{
  background-color: rgba(164,215,234,.5) !important;
}
.pdmmc {
  border-color: #999;
  background-color: #333;
}
.pdmsc:hover {
  color: #a4d7ea;
}
/* カタログdelのダイアログ */
.pddtipc {
  border-color: #999;
  background-color: #4a4a4a;
}

/* 一度開いたスレの背景色 */
td[style='background-color: rgb(255, 204, 153);']{
  background-color: #578 !important;
}
/* 「カタログモード」の背景色をちょっと薄く */
th[bgcolor='#0040e0'] {
  background-color: #2959d1 !important;
}

/* 赤福 */
#akahuku_catalog_reload_container a, #akahuku_catalog_reload_container2 a, #akahuku_catalog_reorder_container a, #akahuku_catalog_reorder_container2 a {
  color: #7bbfd8 !important;
}
.akahuku_markup_catalog_table td a font {
  color: #ccc !important;
}
.akahuku_markup_catalog_table td a.akahuku_visited font {
  background-color: #467 !important;
}
.akahuku_cell {
  color: #ff4034 !important;
}

}

@-moz-document domain("tsumanne.net"), regexp("^https?://(board|kako)\\.futakuro\\.com/.*$"), regexp("^https?://\\w+\\.2ch.n\\.net/(\\w+/.+\\.htm.*|b/)$"), regexp("^https?://.*ftbucket.+") {
/* ---------- スレ ---------- */

/* 「レス送信モード」の背景色をちょっと薄く */
th[bgcolor='#e04000'] {
  background-color: #d15929 !important;
}

/* 書き込みフォームの色付き部分 */
td.ftdc {
  background-color: #222 !important;
}

/* KOSHIAN */
#ftbl {
  background-color: #555 !important;
}
.KOSHIAN_reply_no {
  color: #ccc !important;
}
.KOSHIAN_response {
  background: #4a4a4a !important;
  color: #ccc !important;
}

/* 赤福 */
#ftxa {
  background-image: none !important;
}
.akahuku_status_suffix {
  color: #999 !important;
}
#akahuku_floatpostform_container {
  background: #555 !important;
  border-color: #888 !important;
}
#akahuku_postform {
  border-bottom: none !important;
}
#akahuku_postform_opener_appendix {
  display: none !important;
}
.akahuku_reply_popup {
  background-color: #333 !important;
  border-color: #888 !important;
}
.akahuku_reply_popup td {
  background-color: #4a4a4a !important;
}
#akahuku_commentbox_status_commentbox_rows, #akahuku_commentbox_status_commentbox_bytes {
  color: #aac !important;
}
#akahuku_new_reply_header {
  border-color: #8ae !important;
}
#akahuku_reload_container {
  /*background-color: #4a4a4a !important;*/
  background-color: inherit !important;
  font-size: 95%;
}
#akahuku_reload_button, #akahuku_reload_syncbutton, #akahuku_savemht_button, blockquote > a.akahuku_generated_link, blockquote > small.akahuku_generated {
  color: #7bbfd8 !important;
}
#akahuku_new_reply_header > div {
  border-color: #888 !important;
}
#akahuku_new_reply_header > div > span {
  background-color: #4a4a4a !important;
  border-color: #888 !important;
}
#akahuku_new_reply_header > div > span .akahuku_status_suffix {
  color: #ccc !important;
}
#akahuku_new_reply_header_number {
  color: #d55;
}

/* レス背景色 */
td.rtd {
  background-color: #4a4a4a;
}
/* 題名 */
td.rtd font[color='#cc1105'], span.csb {
  color: #d33 !important;
}
/* 名前 */
td.rtd font[color='#117743'], span.cnm {
  color: #159955 !important;
}
/* 目欄 */
font[color='#005ce6'] {
  color: #49f !important;
}
/* ID・IP */
.idip {
  border-bottom: 1px #ccc dotted;
}
/* レスNo */
.no_quote {
  border-bottom: 1px #ccc dotted;
}
/* delと削除のポップアップ */
.no_quote + .cno {
  background-image:url('data:image/svg+xml;base64,');
}
/* レスNo */
.cno:hover {
  color: #a4d7ea;
}
.no_quote + .cno:hover {
  background-color: rgba(164,215,234,.5);
}
.pdms:hover {
  color: #a4d7ea;
}
.pdmm, .pdd{
  border-color: #999;
  background-color: #333;
}
.pddtip {
  border-color: #999;
  background-color: #4a4a4a;
}
/* NG */
a.fvw_t_ng {
  color:#ccc !important;
}
a.fvw_t_ng:hover {
  color: #a4d7ea !important;
}
/* そうだね */
a.sod {
  color: #ccc !important;
}
a.sod:hover {
  color: #a4d7ea !important;
}
/* 引用 */
font[color='#789922'], span.s3 {
  color: #95bf2a !important;
}
/* 引用のアイコンをちょっと薄く */
.res_pickup_icon:not(.res_pickup_r) {
  filter: invert(90%);
  color: #000;
  font-weight: 700;
}
/* 削除メッセージの色を少し薄く */
blockquote font[color='#ff0000'] {
  color: #f33;
}
/* レスNoと文章選択時のメニュー */
.sub_menu {
  border-color: #333;
}
.sub_contents:hover {
  background-color: #cde;
}

/* レスポップアップ */
.fvw_respop {
  background-color: #333 !important;
  color: #ccc;
}
/* あいまいの文字をちょっと濃く */
.fvw_respop > blockquote + div {
  opacity: 0.5 !important;
}

/* 新着レス数 */
.nb_left {
  background-color: #333;
}

/* スレ消滅時間とかの枠線の色 */
#border_area {
  border-color: #999;
}
/* [MHT保存]の文字色 */
#mht_dl {
  color: #7bbfd8;
}

/* futaba log search */
#futaba_log_search_loglist_button {
  color: #7bbfd8;
}
#futaba_log_search_loglist_button:hover {
  color :#a4d7ea;
}

/* ふたボード */
#threadsbox #t1 td {
  background-color: #333 !important;
}
}

@-moz-document regexp("^https?://dec\\.2chan\\.net/up\\d*/.*$") {
/* ---------- あぷ(小) ---------- */

a:hover {
  background-color: #557;
}

td[bgcolor='#eeaa88']{
  background-color: #dd9977;
}

td > strong > font {
  color: #702222;
}

.files .odd {
  background-color: #4a4a4a;
}

.files tr:hover {
  background-color: #66666F;
}

}

@-moz-document regexp("^https?://(board|kako)\\.futakuro\\.com/.*$") {
/* ---------- ふたクロのログとか固有のやつ ---------- */

th, td, div {
  color: #ccc;
}
#posttable th {
  background-color: #222;
}
#threadsbox td {
  background-color: #4a4a4a;
}
hr + pre {
  background-color: #4a4a4a !important;
}
}

@-moz-document regexp("^https?://\\w+\\.ftbucket\\.info/scrapshot/logmenu\\.htm$") {
/* ---------- ftbucketの何か ---------- */

body>font[size='2'], a[target='_blank']>img{
  display: none !important;
}
}

@-moz-document domain("tsumanne.net") {
/* ---------- 「」ッチー関連  ---------- */

h1 a {
  background-color: #333;
}

/* スレリスト */
table#logs {
  border: solid 1px #888;
}
table#logs th, table#logs td {
  border: solid 1px #888;
}
dl.log {
  border-top: solid 1px #888;
}
table#logs tr:hover td, dl.log:hover {
  background-color: #555;
}

/* タグリスト */
#index {
  background-color: #333;
  border-left: solid 1px #888;
}
#index li a:hover {
  background-color: #777;
}

/* レス背景色 */
td[bgcolor='#f0e0d6'] {
  background-color: #4a4a4a;
}
}
