css:
@-moz-document domain("shopee.com.my") {
.flex.items-center.TvGNLb {
  overflow-y: scroll;
    height: 190px;
word-spacing: 2px;
background-color:#ae2004c7;
padding:10px;
padding-right:0px;
margin-left:-40px;
text-align:justify;
line-height:7px !important;
border-radius:40px 0px 0px 40px}

.flex.items-center.TvGNLb:hover { animation:scale-move 2s ;
         animation-fill-mode: forwards; height: 400px;margin-top:47px;
  position: fixed;
  top: 0;right:0;z-index:10000;margin-right:50px;
}

@keyframes scale-move  {
   0% {
      transform:translate(0,0) scale(1,1);
   }
   
   100% {
      transform:translate(0,0) scale(1.09,1.09);
   }

}
.product-variation { border-radius:13px;font-size:16px !important;}
.product-variation:hover {
  color: var(--darkreader-text--brand-primary-color, #ef5a3d);
  border-color: var(--darkreader-border--brand-primary-color, #9d250d);
  border-radius: 10px;
    
}
}
