@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("basilmarket.com") {



/*ROUNDED EDGES*/

* {-moz-border-radius:4px;-webkit-border-radius:4px; !important;}



/*MISCELLANEOUS EDITS*/

#bodyWrap {padding-bottom:30px !important;
border:1px solid #AAAAAA !important;}

.rightColumn {display:none !important;}

.left {display:none !important; background-color:#135586 !important;}

.leftColumn {display:none !important;}

.right {border:1px solid #10456B !important; background-color:#135586 !important; color:#FFF !important;}

.mb10 {color:#FFF !important;}

div.sep a, div.sep a:link, div.sep a:visited, div.sep a:active {display:none !important;}

div.sep {display:none !important;}

.right a, .right a:visited {color:#FFF !important;}

.likeit {display:none !important;}

.sbc1 {text-transform:capitalize !important; border:#C4810D 1px solid !important;}
.sbc2 {text-transform:capitalize !important; border:#777777 1px solid !important;}
.sbc3 {text-transform:capitalize !important; border:#1376A0 1px solid !important;}
.sbc4 {border:#999891 1px solid !important;}
.sbc5 {text-transform:capitalize !important; border:#3D7F00 1px solid !important;}
.sbc6 {text-transform:capitalize !important; border:#D3D3D3 1px solid !important;}

.innerMenuWrap.menu a.rf[href="#"] {display:none !important;}

.topMsg {background-color:#FDF082 !important; border:1px solid #CCAA00 !important;}

.topicBar {border:1px solid #10456B !important;
padding:5px !important;}

.title3 {font-size:13px !important;}

.userMenu {border-bottom:1px solid #135586 !important; border-left:1px solid #135586 !important; border-right:1px solid #135586 !important;}

.cardHeader {border:1px solid #42918E !important;}

.cardInfo1 {border:1px solid #3C8AC8 !important;}

.pollv {background:#4B92C6 !important;}

.copyright {display:none !important;}

.mb5 {border:1px solid #5a8043 !important;}

#searchareawrap {background:none !important;}

#searchareawrap select {color:#FFFFFF !important;
font-size:11.5px !important;}

#searchareawrap option {font-size:11.5px !important;}

.mm {font-size:14px !important;}

.postn {border:1px solid #AAAAAA !important;}



/*REMOVE ADS*/

.rightModule div.np.mb10, div[style="margin: 0pt 0pt 0pt 10px; padding: 0pt; background: transparent url(img/sadad300.gif) no-repeat scroll center center; min-width: 300px; min-height: 250px; -moz-background-clip: border; -moz-background-origin: padding; -moz-background-inline-policy: continuous;"], .center.mb5, div[style="border-top: 1px solid rgb(204, 204, 204); border-bottom: 1px solid rgb(204, 204, 204); text-align: center; margin-top: 5px; margin-bottom: 5px;"], iframe, div[style="margin: 0pt 0pt 10px 10px; float: right;"]{display: none !important;}


.pbadge, .sbc2, .sbc1, .sbc3, .sbc4, .sbc5 {background-image:none !important;
-moz-border-radius:4px;}



}
