css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain(freekick.org){



body{
	background-color: #111;	
	color: #fff;
}

ins{
	display: none !important;
}

.width{
	background-color: transparent;
	min-width: 1111px;
}

#page-wrap{
	width: auto;
}

#top-wrap{
	background-image: none;
}

#top-info{
	background-image: none;
}

#white-content{
	background-color: transparent;
	background-image: none;
	margin: 0px;
}

#main-tabs{
	margin: 0px;
}

#main-content{
	padding-right: 0px;
	margin: 0px;
}

#main-green-wrap{
	background-image: none;
	margin: 0px;
}

#content-l{
	background-color: #141;
	width: 78%;
	border: 0px;
	padding: 0px;
}

#content-r{
	width: 20%;
	border: 0px;
}

#main-tabs li.tab-active{			
	background-color: #141;
	background-image: none;
}

#main-tabs li.tab-active a{						
	color: #fff;
}

#main-tabs li.tab-hidden{			
	background-color: #222;
	background-image: none;
}

#main-tabs li.tab-hidden a{						
	color: #fff;
}

#green-round{
	display: none;
}

#flashcontentLeft{
	display: none
}

#top-info{
	position: absolute;
	top: 100px;
}

#logo{
	position: absolute;
	top: 25px;
}

#footer-tabs{
	background-image: none;
}

.odd, tr.odd td {
	background-color: #131;
}
.even, tr.even td {
	background-color: #141;
}

.btm-shadow{
	background-image: none;
}

.rigth-crn{
	display: none;
}

.header-sprite{
	background-color:#111;
	background-image: none;
}

.menu-sprite{
	background-image: none;
}

.darkGrey{
	display: none;
}

.section-head{
	padding: 5px;
	color: #fff;
}

.red-stripe{
	display: none;
}

.textlink, .textlink a, .player, .club, .user, .nation, .matchreport-player, .matchreport-club{
	color:#fff !important;
}

table th{
	background-color: #141 !important;
}

#left-wrap{
	margin-top: 150px;
}

.matchreport_legendary {color: #eb3;}
.matchreport_unbelievable {color: #e92;}
.matchreport_masterful {color: #e72;}
.matchreport_awesome {color: #D61;}
.matchreport_brilliant {color: #F83;}
.matchreport_superb {color: #F96;}
.matchreport_excellent {color: #F9C;}
.matchreport_good {color: #C3C;}
.matchreport_decent {color: #ccc;}
.matchreport_weak {color: #3FF;}
.matchreport_poor {color: #FF0;}
.matchreport_awful {color: #F00;}
.matchreport_dreadful {color: #a00;}
.matchreport_prepared {color: #d61;}
.matchreport_totally_free {color: #d91;}
.matchreport_net_wide_open {color: #eb1;}
.matchreport_there {color: #f9c;}
.matchreport_close {color: #3ff;}
.matchreport_too_late {color: #F00;}
.matchreport_right_spot {color: #d61;}
.matchreport_ready {color: #f9c;}
.matchreport_hesitant {color: #c3c;}
.matchreport_fooled {color: #3ff;}
.matchreport_totally_wrong {color: #f00;}
.awesome{color: #D61;}
.brilliant{color: #F83;}
.superb{color: #F96;}
.excellent{color: #F9C;}
.good{color: #C3C;}
.decent{color: #FFF;}
.weak{color: #3FF;}
.poor{color: #FF0;}
.awful{color: #F00;}

}
