css:
@-moz-document url-prefix("https://www.youtube.com/") {
#top-level-buttons-computed > * > a {
    flex-direction: column;
    font-weight: lighter;
    font-size: smaller;
}

#voice-search-button, ytd-button-renderer, yt-chip-cloud-chip-renderer {
    border: none !important;
    border-radius: 0;
    --yt-spec-badge-chip-background: rgba(0, 0, 0, 0.03);
    font-weight: lighter;
}

ytd-live-chat-frame {
    --ytd-margin-6x: 3px;
    border: none;
}

#chat-messages.yt-live-chat-renderer, #contents.yt-live-chat-renderer, #item-list.yt-live-chat-renderer {
	box-shadow: inset 2px 0px 4px 2px rgba(0,0,0,0.1);
}

yt-live-chat-text-message-renderer[author-is-owner]{
    background: none;
}

#container.ytd-searchbox {
    border-radius: 0;
    border: none;
}

ytd-searchbox[has-focus] #container.ytd-searchbox {
    border: none;
}

#voice-search-button.ytd-masthead {
    border-radius: 0;
}

#search-icon-legacy.ytd-searchbox {
    border: none;
}

ytd-searchbox[has-focus] #search-icon.ytd-searchbox {
    display: none;
}

#container.ytd-searchbox, 
ytd-searchbox[has-focus] #container.ytd-searchbox {
    margin: 0;
    padding: 2px 10px;
}

#sponsor-button * {
    --yt-endpoint-color: #a0a0a0;
    --yt-spec-call-to-action: #a0a0a0;
    --yt-button-color: #a0a0a0;
    border: none;
}

ytd-thumbnail-overlay-time-status-renderer {
    margin: 0;
    --yt-spec-static-overlay-background-heavy: #000;
    border-radius: 0;
}

body > div.gstl_50.sbdd_a > div:nth-child(2) > div.sbdd_b {
    box-shadow: none;
    -webkit-box-shadow: none;
	border: none;
}

#logo-icon {
    filter: grayscale(100%)
}

}
