css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('linkedin.com') {
/*
 *
 * GLOBAL
 *
 */
/* applies font to all generic HTML tags */
        *,
        html,
	body,
	span,
	div,
	td,
	br,
	input,
	font,
	button,
	select,
	option
  {
    font-family: 'Satoshi', 'Barlow', arial !important;
    font-weight: 500;
  }
  .ad-banner-container { display: none !important; }
}
