css:
@-moz-document url-prefix("https://myanimelist.net/") {
.page-common #content{
    background-color: #343434
}

.page-common #contentWrapper{
    background-color: #343434
}

body.index .widget-container .widget .widget-header{
    color: #fff;
    border-color: #a12f31
}

.page-common #headerSmall{
    background-color: #343434
}

.page-common .header-profile.link-bg{
    background-color: #343434
}

.page-common .header-profile .header-profile-dropdown ul li a{
    background-color: #343434;
	border-bottom: #a12f31 1px solid
}

.page-common #menu{
    background-color: #a12f31
}

.page-common .h1{
    background-color: #f5e1e7
}

.page-common .borderClass{
    border-color: #a12f31
}

.page-common a, .page-common a:visited{
    color: #f9060a
}

.empty{
    color: #bfbfbf
}

body.index .widget-container .widget.my_statistics table td:nth-child(1){
    color: #bfbfbf
}

.page-common .header-profile-link{
    color: #c6393c
}

p.borderClass{
    color: #bfbfbf
}

body:not(.ownlist){
    background-color: #343434!important
}

body.index .widget-container .widget .index_h2_seo a{
    color: #fff
}

.page-common h2{
    color: #fff
}

td{
    color: #fff
}

.page-common .header-profile .header-profile-dropdown{
    background-color: #343434;
	border: #a12f31 2px solid
}

.page-common .header-list-button{
    color: #a12f31
}

.page-common .header-message-button{
    color: #a12f31
}

.page-common .header-notification-button{
    color: #a12f31
}

.page-common #searchBar.searchBar #topSearchValue{
     background-color: #343434;
	 color: #bfbfbf
}

.page-common .inputtext{
    border-color: #343434
}

.page-common #menu_right{
    color: #a12f31
}

.page-common #searchBar.searchBar #topSearchText{
    border-left: #a12f31 1px solid;
    background-color: #343434;
    color: #bfbfbf
}

.ranking-digest .ranking-header{
    background-color: #a12f31
}

.page-common .content-container .container-right h2{
    color: #fff
}

.page-common .content-container .container-right h5{
    color: #fff
}

.page-common .fn-grey2{
    color: #bfbfbf!important
}

.profile .user-profile-about{
    color: #fff
}

.page-common .fs12{
    color: #fff
}

span.di-ib.fl-r.lh10{
    color: #fff
}

li.clearfix.mb12{
    color: #fff
}

.ranking-digest{
	background-color: #4d4d4d
}

.user-profile .user-status li{
   background-color: #4d4d4d 
}

.user-profile .user-status li:nth-of-type(even){
	background-color: #595959
}

.widget-slide-block .widget-slide .btn-anime{
    border: 0
}

#widget-manga-store .widget-slide .btn-anime{
    border: 0
}

#widget-manga-store .widget-slide .btn-anime .link{
    border: 0
}

.widget-slide-block .widget-slide .btn-anime .external-link{
    background-color: transparent
}

.widget.featured .news-list.widget .news-unit .text, .widget.news .news-list.widget .news-unit .text{
	color: #fff
}

.page-common .reviews_p{
    color: #fff
}

div.spaceit{
    color: #fff
}

li.clearfix{
    color: #fff
}

.anime-detail-header-stats .stats-block{
    background-color: #4d4d4d
}

.anime-detail-header-stats .user-status-block{
	background-color: #595959
}
}
