css:
@-moz-document url-prefix('https://lks.bmstu.ru' ){
  	body {
    background-color: #1c1c1c;
    color: #a6a764;
}
a {
    color: #5397fd;
}
tbody{
    background-color: #224456;
}
.bg-success {
    background-color: #16212c;
}
.bg-info {
    background-color: #1c5187;
}
.text-success {
    color: #7c7c68;
}
.text-info {
    color: #cfcf90;
}

.panel {
    margin-bottom: 20px;
    background-color: #fff0;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #173c4c66;
}
a.list-group-item, button.list-group-item {
    color: #dfc2c2;
}
.list-group-item {
    position: relative;
    display: block;
    padding: 10px 15px;
    margin-bottom: -1px;
    background-color: #2f3d5c;
    border: 1px solid #ddd0;
}
.panel-default>.panel-heading {
    color: #000;
    background-color: #00d5ff;
    border-color: #000;
}

.card {
    background-color: #235219;
}

.portal3hat .p3h_menu-drawer {
    background: #9f723b;
}
}
