css:
@-moz-document domain("v2ex.com") {
  .background-grid {
    background-image: linear-gradient(to right, #ccc8b1 1px, rgba(204, 200, 177, 0) 1px), linear-gradient(to bottom, #ccc8b1 1px, rgba(204, 200, 177, 0) 1px);
    background-size: 0.3rem 0.3rem;
    background-repeat: repeat;
  }
  body {
    color: #454138;
    letter-spacing: 0.03rem;
    font-weight: lighter;
    font-family: helvetica, sans-serif;
    background-color: #d1cdb7;
    caret-color: #454138;
    caret-shape: underscore;
  }
  div[id][class][data-id][style] {
    display: none;
  }
  .sidebar_compliance {
    background-color: #d1cdb7;
  }
  .sidebar_compliance a[href="/advertise"] {
    display: none;
  }
  ::selection {
    background-color: #bab5a1;
  }
  ::-webkit-input-placeholder,
  ::-moz-placeholder,
  ::-ms-input-placeholder {
    opacity: 1;
    -ms-filter: none;
    filter: none;
    color: #bab5a1;
    font-weight: lighter;
  }
  h2,
  h3,
  h4,
  h5,
  h6 {
    letter-spacing: 0.1rem;
    box-sizing: border-box;
  }
  h2 {
    border: solid #454138;
    border-width: 0.1rem 0;
    padding: 0.1rem 1rem;
  }
  hr {
    background-color: #454138;
  }
  strong {
    color: #454138;
  }
  strong.green,
  strong.snow {
    color: #454138;
  }
  strong.online {
    padding: 4px 5px;
    color: #bab5a1;
    border-radius: 1px;
    background: none;
    background-color: #454138;
  }
  pre {
    padding: 1rem;
    background-color: #dcd8c0;
  }
  img.avatar {
    border-radius: 1px;
  }
  .fade,
  .gray {
    color: #454138;
  }
  .fade.small {
    position: relative;
  }
  .fade.small img {
    visibility: hidden;
  }
  .fade.small:after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 14px;
    height: 14px;
    mask: url(https://www.v2ex.com/static/img/heart_neue_red.png?v=16ec2dd0a880be6edda1e4a2e35754b3) no-repeat center / 100%;
    -webkit-mask: url(https://www.v2ex.com/static/img/heart_neue_red.png?v=16ec2dd0a880be6edda1e4a2e35754b3) no-repeat center / 100%;
    background-color: #454138;
  }
  .sep10 + .fade.small:after {
    display: none;
  }
  .topic_info {
    color: #454138;
    font-weight: lighter;
  }
  #Top {
    background-color: #d1cdb7;
  }
  #Top #Logo {
    filter: drop-shadow(0.3rem 0.3rem 0 #bab5a1);
  }
  #Top .tools a.top:link,
  #Top .tools a.top:visited {
    color: #454138;
  }
  #Top .tools a.top:hover {
    text-decoration: underline;
  }
  .alt {
    background-color: #d1cdb7;
  }
  .message {
    background-color: transparent;
    border: solid #bab5a1;
    border-width: 0 0.2rem 0 0.6rem;
    color: #454138;
  }
  #search-container {
    background-color: #dcd8c0;
    border: none;
    border-radius: 1px;
  }
  #search-container:before {
    width: 18px;
    mask: url(https://www.v2ex.com/static/img/search_icon_light.png) no-repeat center / 100%;
    -webkit-mask: url(https://www.v2ex.com/static/img/search_icon_light.png) no-repeat center / 100%;
    background: none;
    left: 5px;
    background-color: #bab5a1;
  }
  #search-container.active:before {
    background-color: #454138;
  }
  #search-container #search-result {
    background-color: #bab5a1;
  }
  #search-container #search-result .search-item {
    color: #454138;
    border-radius: 1px;
  }
  #search-container #search-result .search-item.active {
    background-color: #454138;
    color: #bab5a1;
  }
  #Wrapper {
    background-color: #d1cdb7;
    background-image: linear-gradient(to right, #ccc8b1 1px, rgba(204, 200, 177, 0) 1px), linear-gradient(to bottom, #ccc8b1 1px, rgba(204, 200, 177, 0) 1px);
    background-size: 0.3rem 0.3rem;
    background-repeat: repeat;
  }
  #SecondaryTabs,
  .box {
    background-color: #d1cdb7;
    border-color: #454138;
    border-width: 0.1rem;
    border-radius: 1px;
  }
  #SecondaryTabs .header,
  .box .header {
    border-color: #454138;
  }
  #SecondaryTabs .note_item,
  .box .note_item {
    background-color: #d1cdb7;
    color: #454138;
    border-color: #454138;
    transition: all 0.2s;
  }
  #SecondaryTabs .note_item .note_item_info,
  .box .note_item .note_item_info {
    color: #454138;
  }
  #SecondaryTabs .note_item .note_item_title a:link,
  .box .note_item .note_item_title a:link,
  #SecondaryTabs .note_item .note_item_title a:active,
  .box .note_item .note_item_title a:active,
  #SecondaryTabs .note_item .note_item_title a:visited,
  .box .note_item .note_item_title a:visited {
    color: #454138;
  }
  #SecondaryTabs .note_item:hover,
  .box .note_item:hover {
    background-color: #454138;
    color: #bab5a1;
  }
  #SecondaryTabs .note_item:hover .note_item_info,
  .box .note_item:hover .note_item_info {
    color: #bab5a1;
  }
  #SecondaryTabs .note_item:hover .note_item_title a:link,
  .box .note_item:hover .note_item_title a:link,
  #SecondaryTabs .note_item:hover .note_item_title a:active,
  .box .note_item:hover .note_item_title a:active,
  #SecondaryTabs .note_item:hover .note_item_title a:visited,
  .box .note_item:hover .note_item_title a:visited {
    color: #bab5a1;
  }
  .vote {
    border-radius: 1px;
  }
  .vote:link {
    color: #454138;
    border: 0;
    background-color: #dcd8c0;
    border-radius: 1px;
  }
  .vote:active {
    box-shadow: none;
  }
  .cell {
    border-color: #454138;
    border-width: 0.1rem;
    border-radius: 1px;
  }
  .cell.page-content-header {
    background-color: #454138;
    color: #bab5a1;
  }
  .cell.page-content-header a {
    color: #bab5a1;
  }
  .cell.page-content-header a:hover {
    color: #bab5a1;
  }
  .cell.page-content-header .topic-count {
    color: #bab5a1;
  }
  .cell.page-content-header .topic-count strong {
    font-weight: normal;
    color: #bab5a1;
  }
  .cell:last-child {
    border-color: #454138;
    border-width: 0.1rem;
  }
  .cell .fr .thank_area a {
    color: #454138 !important;
    background-color: transparent;
  }
  .cell .fr .thank_area a.thank[href="#;"] {
    line-height: 1.3;
    padding: 0 2px;
    border-radius: 1px;
  }
  .cell .fr .thank_area a.thank[href="#;"]:hover {
    background-color: #454138;
    color: #dcd8c0 !important;
  }
  .cell .fr > a[href="#;"] {
    position: relative;
  }
  .cell .fr > a[href="#;"]:after {
    content: "";
    width: 20px;
    height: 20px;
    position: absolute;
    top: 0;
    left: 0;
    mask: url(https://www.v2ex.com/static/img/reply_neue.png) no-repeat center / 100%;
    -webkit-mask: url(https://www.v2ex.com/static/img/reply_neue.png) no-repeat center / 100%;
    background-color: #bab5a1;
  }
  .cell .fr > a[href="#;"] img {
    visibility: hidden;
  }
  .cell .thanked {
    color: #bab5a1;
    background-color: #454138;
    border-radius: 1px;
    line-height: 1.3;
    padding: 0 2px;
    cursor: default;
  }
  .cell.alt {
    background-color: #d1cdb7;
  }
  .cell[style="background-color: #f0f0f0;"] {
    background-color: #d1cdb7 !important;
  }
  .cell_ops {
    background-color: #d1cdb7;
  }
  #Tabs {
    background-color: #d1cdb7;
  }
  #Tabs .tab {
    color: #454138;
  }
  #Tabs .tab:hover {
    background-color: #dcd8c0;
  }
  #Tabs .tab_current {
    background-color: #454138;
    color: #bab5a1;
    border-radius: 1px;
  }
  .topic_buttons {
    background: none;
    background-color: #d1cdb7;
  }
  .topic_buttons .topic_stats {
    color: #454138;
    text-shadow: none;
  }
  .topic_buttons .tb:link,
  .topic_buttons .tb:visited {
    color: #454138;
    text-shadow: none;
    border-radius: 1px;
    transition: all 0.2s;
  }
  .topic_buttons .tb:link:hover,
  .topic_buttons .tb:visited:hover {
    background-color: #dcd8c0;
  }
  a:link,
  a:active,
  a:visited {
    color: #454138;
    line-height: normal;
  }
  a.node {
    transition: all 0.2s;
  }
  a.node:link,
  a.node:active {
    background-color: #dcd8c0;
    color: #454138;
  }
  a.node:visited {
    background-color: #bab5a1;
    color: #454138;
  }
  a.node:hover {
    color: #bab5a1;
    background-color: #454138;
  }
  a.topic-link:link,
  a.topic-link:active {
    color: #454138;
  }
  a.topic-link:visited {
    color: #454138;
    font-weight: lighten;
  }
  a.count_livid {
    position: relative;
  }
  a.count_livid:link,
  a.count_livid:active {
    background-color: #dcd8c0;
    color: #454138;
    border-radius: 1px;
    padding: 4px 0px;
    width: 42px;
    text-align: center;
    transition: all 0.2s;
  }
  a.count_livid:visited {
    background-color: #454138;
    color: #bab5a1;
  }
  a.light-toggle:link,
  a.light-toggle:visited {
    display: none;
  }
  a.dark:visited,
  a.dark:active,
  a.dark:link,
  a.dark:hover {
    color: #454138;
  }
  a[href="/write"] img {
    filter: sepia(90%) brightness(0.93);
  }
  a.tag:visited,
  a.tag:link {
    border-radius: 1px;
    background-color: #454138;
    color: #bab5a1;
    padding: 5px 8px;
    transition: all 0.2s;
  }
  a.tag:visited:hover,
  a.tag:link:hover {
    background-color: #bab5a1;
    color: #454138;
  }
  a.tab:active,
  a.tab:link,
  a.tab:visited {
    border-radius: 1px;
  }
  a.tab:hover {
    background-color: #454138;
    color: #bab5a1;
  }
  a.page_current {
    border: 0;
    background-color: #454138;
    color: #bab5a1;
    border-radius: 1px;
    font-weight: bold;
    box-shadow: none;
    line-height: 18px;
  }
  a.page_normal:visited,
  a.page_normal:link {
    border: 0;
    border-radius: 1px;
    font-weight: normal;
    line-height: 18px;
    background-color: #bab5a1;
    color: #454138;
    padding: 3px 6px;
    margin: 0 1px;
  }
  a.tab_current:visited,
  a.tab_current:link {
    background-color: #454138;
    color: #bab5a1;
    border-radius: 1px;
  }
  .ago {
    color: #454138;
  }
  .subtle,
  .outdated {
    background-color: transparent;
    color: #454138;
    padding: 0.5rem;
    padding-left: 1.5rem;
    margin: 0.5rem;
    position: relative;
    border: 0;
    box-sizing: border-box;
  }
  .subtle:before,
  .outdated:before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    height: 100%;
    width: 0.2rem;
    border: solid #bab5a1;
    border-width: 0 0.2rem 0 0.5rem;
  }
  .badges .badge.op {
    color: #454138;
    border-color: #454138;
  }
  span.ago {
    color: none;
  }
  span.no {
    border-radius: 1px;
    background-color: #bab5a1;
    color: #454138;
  }
  span.snow {
    color: #454138;
  }
  span.hljs-string {
    color: #454138;
    font-weight: normal;
  }
  .tab-alt .counter {
    background-color: #454138;
    color: #bab5a1;
    border-radius: 1px;
    font-weight: normal;
    padding: 2px 4px;
  }
  .tab-alt:hover {
    border-color: #454138;
  }
  .item_hot_topic_title {
    text-shadow: none;
  }
  .bigger {
    color: #454138;
  }
  #money .balance_area {
    background-color: #dcd8c0;
    border-radius: 1px;
    color: #454138;
    font-weight: bold;
    text-shadow: none;
    transition: all 0.2s;
  }
  #money .balance_area:hover {
    background-color: #454138;
    color: #bab5a1;
  }
  #member-activity .member-activity-bar {
    background-color: #dcd8c0;
  }
  #member-activity .member-activity-bar .member-activity-start,
  #member-activity .member-activity-bar .member-activity-half,
  #member-activity .member-activity-bar .member-activity-fourth,
  #member-activity .member-activity-bar .member-activity-almost {
    background-color: #454138;
  }
  #Main .sep20:nth-of-type(4) {
    display: none;
  }
  #Main .sep20:nth-of-type(3) + div[style="box-sizing: border-box"] {
    display: none;
  }
  #Rightbar .sep20:nth-of-type(3) {
    display: none;
  }
  #Rightbar .sep20:nth-of-type(3) + div[style="box-sizing: border-box"] {
    display: none;
  }
  .payload {
    background-color: #dcd8c0;
    color: #454138;
  }
  .inner {
    color: #454138;
    background-color: #d1cdb7;
  }
  .item_node {
    border: .1rem solid #bab5a1;
    border-radius: 1px;
    background-color: #dcd8c0;
  }
  .item_node:hover {
    border: .1rem solid #454138;
    box-shadow: none;
  }
  #workspace {
    border-color: #454138;
  }
  #workspace .CodeMirror .CodeMirror-gutters {
    border-color: #454138;
  }
  #workspace .CodeMirror .CodeMirror-gutter {
    background-color: #d1cdb7;
  }
  #workspace .CodeMirror .CodeMirror-linenumber {
    color: #454138;
  }
  .CodeMirror-line::selection {
    background-color: #bab5a1;
  }
  .CodeMirror-line > span::selection {
    background-color: #bab5a1;
  }
  .CodeMirror-line > span > span::selection {
    background-color: #bab5a1;
  }
  .page_input,
  #reply_content,
  #topic_title,
  #topic_content,
  .sl,
  .ml,
  .select2-selection__rendered,
  .select2-results__option,
  .select2-search__field,
  .status_editor,
  .note_editor,
  .sll,
  .CodeMirror-scroll,
  .page_input {
    background-color: #dcd8c0;
    border: 0;
    outline: 0;
    color: #454138;
    appearance: none;
    transition: all 0.2s ease-out;
  }
  .page_input:focus,
  #reply_content:focus,
  #topic_title:focus,
  #topic_content:focus,
  .sl:focus,
  .ml:focus,
  .select2-selection__rendered:focus,
  .select2-results__option:focus,
  .select2-search__field:focus,
  .status_editor:focus,
  .note_editor:focus,
  .sll:focus,
  .CodeMirror-scroll:focus,
  .page_input:focus {
    color: #454138;
    background-color: #bab5a1;
    border: 0;
  }
  .page_input::selection,
  #reply_content::selection,
  #topic_title::selection,
  #topic_content::selection,
  .sl::selection,
  .ml::selection,
  .select2-selection__rendered::selection,
  .select2-results__option::selection,
  .select2-search__field::selection,
  .status_editor::selection,
  .note_editor::selection,
  .sll::selection,
  .CodeMirror-scroll::selection,
  .page_input::selection {
    background-color: #dcd8c0;
  }
  .select2-dropdown {
    background-color: #d1cdb7;
  }
  .select2-results__option--highlighted,
  .select2-results__option--selected {
    background-color: #454138 !important;
    color: #bab5a1 !important;
  }
  .mll {
    border: none;
  }
  .mll:focus {
    border: none;
  }
  #topic_content::-moz-placeholder,
  #topic_title::-moz-placeholder {
    color: #454138;
  }
  select {
    background-color: #bab5a1;
    color: #454138;
    outline: 0;
    border: 0;
  }
  .super.button,
  .normal.button {
    position: relative;
    background-color: #dcd8c0;
    border-radius: 1px;
    color: #454138;
    border: none;
    outline: none;
    box-shadow: none;
    text-shadow: none;
    transition: all 0.2s linear;
    background-image: none;
  }
  .super.button:hover,
  .normal.button:hover {
    background-color: #454138;
    color: #dcd8c0;
  }
  .super.button:hover:enabled,
  .normal.button:hover:enabled {
    text-shadow: none;
    border: 0;
    outline: 0;
    background-color: #454138;
    color: #dcd8c0;
  }
  .super.button:active:enabled,
  .normal.button:active:enabled {
    background-color: #dcd8c0;
    color: #454138;
    text-shadow: none;
  }
  .disable_now {
    background-color: transparent !important;
    color: #bab5a1;
  }
  td.hover_now {
    background-color: #454138 !important;
    color: #bab5a1 !important;
    text-shadow: 0;
  }
  #reply-box {
    border-color: #454138;
    background-color: #d1cdb7;
  }
  #Bottom {
    background-color: #d1cdb7;
    border-color: #454138;
  }
  #Bottom .fade.small:after {
    display: none;
  }
  #Bottom .inner {
    background-color: #d1cdb7;
  }
  .social_label:link,
  .social_label:visited {
    border-radius: 1px;
    transition: all 0.2s;
    background-color: #bab5a1;
    color: #454138;
  }
  .social_label:link:hover,
  .social_label:visited:hover {
    background-color: #454138;
    color: #bab5a1;
  }
  .social_label:hover {
    background-color: #454138;
    color: #bab5a1;
  }
  .cell_tabs {
    border-color: #454138;
  }
  .cell_tabs .cell_tab:link,
  .cell_tabs .cell_tab:visited {
    color: #454138;
  }
  .cell_tabs .cell_tab:link:hover,
  .cell_tabs .cell_tab:visited:hover {
    border-color: #454138;
  }
  .cell_tabs .cell_tab_current {
    border-color: #454138;
  }
  .dock_area {
    background-color: #bab5a1;
    color: #454138;
  }
  .toggle-more-nodes {
    background-color: #bab5a1;
    color: #454138;
  }
  .onoffswitch label .frame::before .onoffswitch label .frame:before {
    background-color: #454138;
  }
  img[src="/static/img/cloud.png"] {
    filter: sepia(60%) brightness(70%);
  }
  .selectboxit-list {
    background-color: #dcd8c0;
    border: 0;
    color: #454138;
  }
  .selectboxit-list > .selectboxit-focus > .selectboxit-option-anchor {
    color: #bab5a1;
  }
  .radio-group {
    background-color: #d1cdb7;
    color: #454138;
    box-shadow: none;
    border-radius: 1px;
  }
  .radio-group label {
    color: #454138;
    background-color: #d1cdb7;
  }
  .radio-group > input[type="radio"] + label {
    background-color: #bab5a1;
    color: #454138;
    border-radius: 1px;
  }
  .radio-group > input[type="radio"]:checked + label {
    background-color: #454138;
    border-radius: 1px;
    color: #dcd8c0;
  }
  .header-transparent {
    background-color: #454138;
    color: #bab5a1;
  }
  .cell-translucent {
    background-color: #d1cdb7;
    color: #454138;
    border-color: #454138;
  }
}
