css:
@-moz-document domain("subeta.net") {
/*Main*/
 body, html {
    color: #fff!important;
}
a {
    color: #4fff6d!important;
}
.card {
    background-color: #1d1e25;
}
.card-header {
    background-color: rgb(16 82 23);
    color: white;
}
.container-fluid {
    background-color: #1d1e25;
}
#content {
    background-color: #1d1e25;
}
#main-content {
    background-color: #1d1e25;
}
.card-body {
    background-color: #1d1e25;
}
.jumbotron {
    background-color: #1d1e25;
}
.new-news-announcement-banner {
    background-color: #1b5a22!important;
}
/*Side Bar*/
 #sidebar, #main-content {
    background-image: none;
}
#sidebar-inner {
    background-image: none;
}
.sidebar-widget .sidebar-widget-header {
    border: 1px solid #164e0f;
    background-color: #002604;
    color: #ffffff;
}
/*Items*/
 .wishlist {
    border: 5px dashed red;
}
.ui.grid {
    background-color: #1d1e25;
}
.ui.segment {
    background-color: #1d1e25;
}
.ui.attached.header {
    background-color: #002604;
}
.ui.message {
    background-color: #002604;
    color: white;
}
.bg-light {
    background-color: #1d1e25!important;
}
.ui[class*="internally celled"].grid>.row:first-child, .ui[class*="internally celled"].grid>.row>.column:first-child {
    background-color: #1d1e25;
}
.ui.middle.aligned.grid, .ui.middle.aligned.grid > .row > .column, .ui.middle.aligned.grid > .column, .ui.grid .middle.aligned.column, .ui.grid > .middle.aligned.row > .column {
    background-color: #1d1e25;
}
.ui.secondary.pointing.menu .header.item {
    color: #ffffff !important;
}
/*Pets*/
 .pet_stats {
    background-color: #005210!important;
}
/*Friends Feed*/
 .ui.feed .content .summary {
    color: white;
}
.ui.feed .content .date {
    color: white;
}
.feed_menu {
    color: #1d1e25;
}
/*Achievements*/
 .ui.menu {
    background-color: #1d1e25;
}
.ui.text.menu .header.item {
    color: white;
}
.ui.label {
    color: #fff !important;
}
.ui.yellow.message, .ui.warning.message {
    background-color: #1d1e25;
    color: #66ff64;
}
}
