css:
@-moz-document domain("wikipedia.org") {
    body {
        --wiki-width: 70em;
    }


    @media screen and (min-width: 1504px) {
        .mw-body,
        #mw-head {
            display: flow-root;
            max-width: var(--wiki-width);
            margin-left: auto;
            margin-right: auto;
        }

        .mw-body {
            border: 0;
        }

        #mw-head {
            right: unset;
            left: calc(50% - var(--wiki-width)/2 - 1.5em);
            max-width: calc(var(--wiki-width) + 3em);
        }

        .vector-menu-tabs {
            padding-left: 0;
        }


        #mw-head-base,
        #left-navigation,
        #mw-data-after-content,
        .mw-footer {
            margin-left: 0;
        }

        .vector-search-box {
            margin-right: 0;
        }
        #p-personal {
            right: 0;
        }
        .mw-footer {
            max-width: var(--wiki-width);
            margin: 0 auto;
        }
    }
}
