css:
@-moz-document url-prefix("https://es6.ruanyifeng.com/") {
body {
	display: flex;
	height: 100vh;
}

#sidebar {
  position: static;
  height: 100%;
  overflow: auto;
}

#content {
  flex: 1;
  height: 100%;
	overflow: auto;
  padding: 0 400px 0 100px;
}

#content-toc {
  position: fixed;
  top: 50px;
  right: 50px;
  width: 300px;
}

#back_to_top {
	position: fixed;
  margin: 0;
  top: auto;
 	bottom: 90px;
  right: 100px;
}

#edit {
  position: fixed;
  margin: 0;
  top: auto;
  bottom: 70px;
  right: 100px;
}

#theme {
  position: fixed;
  margin: 0;
  top: auto;
  bottom: 50px;
  right: 100px;
}

#flip {
  margin: 0px;
  position: fixed;
  bottom: 10px;
  left: 50%;
  transform: translateX(-50%);
}
}
