css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://dragcave.net") {

.csort2 img{
background: #ab909e !important;
}

td, tr, th img{
background: #767475 !important;
}

table, td, td{
text-align: center !important;
font-weight: normal !important;
font-size: 13px !important;
border-spacing: 5px !important;
}


INPUT {
color:#333 !important;
font:8pt Trebuchet MS !important;
text-transform: inherit !important;
}

TEXTAREA {
height: 17px !important;
color:#333 !important;
font:8pt Trebuchet MS !important;
text-transform: inherit !important;
}

html,body {
	padding: 0 !important;
	margin: 0 !important;
}
body {
	color: #382D3B !important;
	background: #44300B url("http://imgur.com/u0zU5.jpg") !important;
	font-size: 10pt !important;
	font-family: Trebuchet MS !important;
	color: #382D3B !important;
text-transform: lowercase !important;
}
h1,h2 {
	padding: 0 !important;
	margin: 0 !important;
}

b {     
        color:#AEA0A5 !important;font:11px georgia !important;
        font-style:italic !important;
        font-weight:bold !important;letter-spacing:-.5pt !important;
}

p {
	text-indent: 1.5em !important;
}
a:link, a:active, a:visited {
	color:#e0c1d1 !important;
        font:12px georgia !important;
        font-style:italic !important;
}
a:hover {
color:#fff !important;
        background:#767475 !important;
}
th,td {
	text-align: center !important;
}
table.vamid td {
	vertical-align: middle !important;
}
table.leftalign,table.leftalign td,table.leftalign th {
	text-align: left !important;
}
img {
	border-width: 0 !important;
}
acronym {
	cursor: help !important;
}
#wrapper {
	width: 900px !important;
	max-width: 800px !important;
	margin: auto !important;
	text-align: justify !important;
}
#top {
	background: url("http://imgur.com/Wyp7V.png") no-repeat !important;
	width: 825px !important;
	color: #000 !important;
	height: 20px !important;
}
#middle {
	background: url("http://imgur.com/DYELX.png") repeat-y !important;
	padding: 0 60px !important;
	color: #000 !important;
	min-height: 10px !important;
	width: 705px !important;

}
#bottom {
	background: url("http://imgur.com/0gTF3.png") no-repeat !important;
	color: #000 !important;
	width: 825px !important;
	height: 25px !important;
}
#logo {
	display: block !important;
	margin: 0 auto !important;
	width: 562px !important;
	height: 127px !important;
	display:none !important;

}
#menu {
	text-align: center !important;
	margin: 0 auto !important;
}
.cave,.scroll,.login,.register,.shop,.forum,.logout {
	margin: 0 5px !important;
	background: transparent url("http://imgur.com/uDS77.png") no-repeat !important;
	height: 50px !important;

}
.cave {
	width: 97px !important;
}
.scroll {
	width: 92px !important;
	background-position: -95px center !important;
}
.login {
	width: 94px !important;
	background-position: -186px center !important;
}
.register {
	width: 138px !important;
	background-position: -278px center !important;
}
.shop {
	width: 82px !important;
	background-position: -362px center !important;
	display:none !important;
}
.forum {
	width: 113px !important;
	background-position: -417px center !important;
}
.logout {
	width: 123px !important;
	background-position: -530px center !important;
}
#prefooter {
	margin-left: -11.5px !important;
	height: 90px !important;
	overflow: hidden !important;
	width: 728px !important;
	margin-right: -20px;
	display: none !important;
}
#footer {
	font-size: 0.8em !important;
	text-align: center !important;
	margin: 0 auto;
}
}
