css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://forums.eslcafe.com/korea)
{
    body > div tr:nth-child(3),
    body > div tr:nth-child(4),
    body > div center > center,
    div.bannerad,
    [src$="logo_phpBB.gif"] 
    {display: none !important;}

    iframe[width="468"][height="60"],  a img[width="468"][height="60"]
      {
	display: none !important;
    }

    body {
        background-color: #FFFFFF !important;
    }

    A[href^="http://www.culturalextremes.com/"],
    A[href^="http://www.tefllife.com/"],
    A[href^="http://www.eslcafe.com/email.html"],
    A[href^="http://www.phpbb.com/"]
    {display:none !important;}

    table.forumline {
        empty-cells: show !important; 
        border-spacing: 0 !important;
        border: 1px dashed #AAAAAA !important;
        border-collapse: collapse !important;
    }
    table.forumline > tbody > tr > td, table.forumline > tbody > tr > th {
        background: #FFF !important;
        border: 1px dashed #AAAAAA !important;
    }
    .bodyline {
        border: 0 none #FFF !important;
    }
    .maintitle {
        font-size: 1.3em !important;
        font-weight: bold !important;
    }
    .maintitle +  br, .bodyline > table.forumline + table + table + div, .bodyline > table:first-child + br {
        display: none !important;
    }
    a:link {
        color: #990000 !important;
        text-decoration: none !important;
    
    }
    a:visited {
        color: #777 !important;
 
    }
    a:hover, a:focus, a:active {
        color: #111 !important; 
        border-bottom-color: #111 !important;
        text-decoration: underline !important;
    }
    th {
        color: #000 !important;
    }
}
