@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(https://www.mbank.com.pl/),
url-prefix(http://demo.mbank.com.pl/),
url-prefix(https://form.mbank.com.pl/),
url-prefix(http://info.mbank.com.pl/)
{

body
{
background-color: #2a2a2a !important;
}


fieldset *
{
color: #000 !important;
}

#customerInfo *
{
color: #ffffff !important;
}

#bkg, #pageHeader
{
visibility:hidden !important;
}

#header, td[bgcolor$="#ffffff"]
{
        background-color: transparent !important;
        border-bottom: 3px solid #000000 !important;
}
#logonBlock
{
	background-color: transparent !important;
}
TD.tablehead
{
        color: #ffffff  !important;
        background-color: #1d1d1d !important;
}
TD.tableitem, TD.tableitembold, TD.tabledetitem, TD.tabledetitembold, td[bgcolor$="#ffd953"]
{
        color: #ffffff !important;
        background-color: #100f0f !important;
}
TD.tableseparator, td[bgcolor$="#98B0EE"]
{
        background-color: #2a2a2a !important;
}
td[height$="9"], table[bgcolor$="#FFFFDF"]
{
	background-color: transparent !important;
}
#vertLines, td[width$="1"]
{
        display:none;
}
A, A:link, A:visited, A:hover
{
        color: #000 !important;
	background-color: transparent !important;
}
A:focus
{ 
        outline:none;
}
#menu
{
        margin-left: -10px !important;
}
#menu li
{
        background-color: transparent !important;
        background-position: 0px 5px !important;
        margin-top: -2px !important;
        padding-top: 0px !important;
        margin-bottom: 0px !important;
        padding-bottom: 0px !important;
}
#menu a:link, #menu a:visited
{
        color: #ffffff !important;
	background-color: #2c2c2c !important;
        margin-left: -2px !important;
        display: block !important;
        width: 175px !important;
        padding: 2px  !important;
        border-top: 1px solid #1d1d1d !important;
        border-left: 2px groove #1d1d1d !important;
        border-right: 2px groove #1d1d1d !important;
        border-bottom: 2px groove #1d1d1d !important;
}

#menu a:hover, #menu li.sel a:link
{
        color: #ffffff !important;
        background-color: #404040 !important;

}
#menu ul.menud li, #menu ul.menud
{
	background-color: transparent !important;
        background-position: 0px 5px !important;
        margin-top: -2px !important;
        margin-left: -3px !important;
        padding-top: 0px !important;
        margin-bottom: 0px !important;
        padding-bottom: 0px !important;
}
#menu ul.menud a:link, #menu ul.menud a:visited
{
        color: #ffffff !important;
	background-color: #2c2c2c !important;
        margin-left: -2px !important;
        display: block !important;
        width: 165px !important;
        padding: 2px  !important;
        border-top: 1px solid #1d1d1d !important;
        border-left: 2px groove #1d1d1d !important;
        border-right: 2px groove #1d1d1d !important;
        border-bottom: 2px groove #1d1d1d !important;
}
#menu ul.menud a:hover, #menu ul.menud li.sel a:link
{
        color: #ffffff !important;
        background-color: #404040 !important;
}
#menu ul.submn li, #menu ul.submn
{
	background-color: transparent !important;
        background-position: 0px 5px !important;
        margin-top: -2px !important;
        padding-top: 0px !important;
        margin-bottom: 0px !important;
        padding-bottom: 0px !important;
}
#menu ul.submn a:link, #menu ul.submn a:visited
{
        color: #ffffff !important;
	background-color: #2c2c2c !important;
        margin-left: -2px !important;
        display: block !important;
        width: 165px !important;
        padding: 2px  !important;
        border-top: 1px solid #1d1d1d !important;
        border-left: 2px groove #1d1d1d !important;
        border-right: 2px groove #1d1d1d !important;
        border-bottom: 2px groove #1d1d1d !important;
}
#menu ul.submn a:hover, #menu ul.submn li.sel a:link
{
        color: #ffffff !important;
        background-color: #404040 !important;
}
#menu ul.menud ul.submd li, #menu ul.menud ul.submd
{
	background-color: transparent !important;
        background-position: 0px 5px !important;
        margin-top: -2px !important;
        padding-top: 0px !important;
        margin-bottom: 0px !important;
        padding-bottom: 0px !important;
}
#menu ul.menud ul.submd a:link, #menu ul.menud ul.submd a:visited
{
        color: #ffffff !important;
	background-color: #2c2c2c !important;
        margin-left: -2px !important;
        display: block !important;
        width: 158px !important;
        padding: 2px  !important;
        border-top: 1px solid #1d1d1d !important;
        border-left: 2px groove #1d1d1d !important;
        border-right: 2px groove #1d1d1d !important;
        border-bottom: 2px groove #1d1d1d !important;
}
#menu ul.menud ul.submd a:hover, #menu ul.menud ul.submd li.sel a:link
{
        color: #ffffff !important;
        background-color: #404040 !important;
}
TD, span, p
{
	color: black !important;
}
input, select, textarea
{
	color: #000000 !important;
}
hr
{
        display: none !important;
}
}
