css:
@-moz-document url-prefix("https://www.jw.org/")
{

.cc-theme--dark {
        --ct-primaryNav-bgColor: #890000 !important;
}

.cc-theme--light {
        --ct-primaryNav-bgColor: #AE0000 !important;
        --ct-body-bgColor: #222222;
}
.cc-theme--light .wrapperShadow {
    box-shadow: 0 0 2.5em 0.5em rgb(0 0 0 / 50%)
}

.primaryNavItem>a:hover, .primaryNavItem>a:focus {
    background-color: #550000;
}


.homeCallToActionItem .homeCallToActionItem--image > a > img {
    transition: all 2s ease-in-out 1s;
}
.homeCallToActionItem:hover .homeCallToActionItem--image > a > img {
    border-radius: 50% 20% 50% 20% !important;
    box-shadow: 0 0 1.2em -0.25em #88000080;
    transition-delay: 250ms;
}

.synopsis .syn-img img, .homeTopicGrid .homeTopicItem > div {
    border-radius: 2em;
    box-shadow: 0 0 1.2em -0.2em #77777750;
    transform: rotate(0deg);
    transition: all 500ms ease-in-out 250ms;
}
.synopsis:hover .syn-img img, .homeTopicGrid .homeTopicItem:hover > div {
    border-radius: 0.2em;
    transform: rotate(-3deg);
    box-shadow: 0 0 1.2em 0.2em #77777750;
}

.homeTopicGrid .homeTopicItem {
    outline: 1px white;
}


#siteLogo {
    animation: move 20s ease infinite;
    filter: hue-rotate(-237deg) brightness(0.98);
}

@keyframes move {
  0% {  transform: rotate(0deg); }
  85% {  transform: rotate(0deg);}
  90% { transform: rotate(-25deg); filter: hue-rotate(-237deg) brightness(0.92); -webkit-filter: hue-rotate(-237deg) brightness(0.98) blur(0px);  }
  92% { transform: rotate(15deg); filter: hue-rotate(-237deg) brightness(0.92); -webkit-filter: hue-rotate(-237deg) brightness(0.92) blur(1px); }
	94% { transform: rotate(-7deg); filter: hue-rotate(-237deg) brightness(0.92); -webkit-filter: hue-rotate(-237deg) brightness(0.88) blur(0.8px); }    
    96% { transform: rotate(5deg); filter: hue-rotate(-237deg) brightness(0.92); -webkit-filter: hue-rotate(-237deg) brightness(0.90) blur(0.5px); }    
    98% { transform: rotate(-2deg); filter: hue-rotate(-237deg) brightness(0.92); -webkit-filter: hue-rotate(-237deg) brightness(0.92) blur(0.2px); }    
  100% { transform: rotate(0deg); }
}
  
}
