css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://muzak:911/") {

html {font-family: arial, verdana !important; text-shadow: #000000 1px 1px 3px, #000000 -1px -1px 3px;}

body {background: url("http://i64.photobucket.com/albums/h180/ragingshinigami/tech3.jpg") #222222 fixed repeat left top!important; color:#ffffff !important; }

table {background: rgba(20,20,20,.4) !important;-moz-column-count:2 !important;}

tr[bgcolor="#d1d5d7"] {background: url("http://lh3.google.com/preston.scheuneman/R1m-ja98gjI/AAAAAAAAADI/KGg9nz8KuPI/GlassShiny.png") fixed repeat left center !important; border-color: #000000 !important; }

a {color: #99ccff !important;}
a:visited {color: #6699ff !important;}
a:hover {text-shadow: #ffffff 0px 0px 3px !important;}
.isnew{color: #ff6666 !important;}


p > a img {-moz-box-shadow: #000000 0px 0px 3em 1px !important; width: 300px !important; height: 300px !important;}

p > a img:hover {-moz-box-shadow: #99ccff 0px 0px 3em 4px !important; }

p:nth-child(2) {min-width: 350px; max-width: 700px !important; float:left;}
p:nth-child(3), p:nth-child(4) {width: 700px !important;float:left !important;}

hr {display: none !important;}
}
