css:
@-moz-document url-prefix("https://news.ycombinator.com/") {
/* Globals  */
 body, textarea, input {
    font-family: IBMPlexSans, Arial, sans-serif;
    background: #141414 !important;
    color: #b2b6b9 !important;
}
.hnname {
    margin-right: 15px;
    margin-left: 5px;
}
a:link {
    color: #d3d6d9 !important;
}
a:visited {
    color: rgb(111, 112, 113) !important;
}
br {
    line-height: 1.1;
}
table#hnmain {
    margin-top: 20px;
    max-width: 1024px;
    background-color: rgba(125, 124, 124, 0);
}
td {
    font-size: 12pt;
    line-height: 16pt;
}
textarea, input {
    padding: 10px;
    border: 2px solid #2b2b2b;
}
.itemlist .athing a:link {
    color: #ddd6d6 !important
}
.pagetop a:visited {
    color: #fff !important;
}
td[bgcolor='#ff6600'] {
    background-color: transparent !important;
    padding: 0 0 7px 0;
}
/* Main News Feed  */
 .title {
    color: #b2b6b9 !important;
    padding-left: 10px;
    padding-right: 10px;
    font-family: IBMPlexSans, Arial, sans-serif;
}
.title a {
    font-size: 13pt;
    color: #b2b6b9;
    line-height: 1;
    font-weight: 500;
    font-size: 18px;
    font-family: IBMPlexSans, Arial, sans-serif;
}
span.sitebit.comhead a > span {
    color: rgb(129, 131, 132) !important;
    font-size: 12px;
    font-family: IBMPlexSans, Arial, sans-serif;
}
.subtext, .subtext a, .subtext .score, .subtext .age a {
    color: rgb(129, 131, 132) !important;
    font-size: 12px;
    font-family: IBMPlexSans, Arial, sans-serif;
}
.subtext {
    padding-left: 10px;
}
tr.spacer {
    height: 0 !important;
    background-color: transparent !important;
}
tr.spacer:nth-child(2n + 1) {
    box-shadow: 0 1.7px 1.4px rgba(0, 0, 0, 0.112), 0 4px 3.3px rgba(0, 0, 0, 0.162), 0 7.5px 6.3px rgba(0, 0, 0, 0.2), 0 13.4px 11.2px rgba(0, 0, 0, 0.238), 0 25.1px 20.9px rgba(0, 0, 0, 0.288), 0 60px 50px rgba(0, 0, 0, 0.4);
}
tr.athing td {
    padding-top: 12px;
    border-top: none;
}
tr.athing.comtr td * td {
    border-top: none;
    padding-top: 2px;
}
td.subtext {
    padding-top: 0;
}
.itemlist {
    border: 1px solid #343536;
    border-radius: 4px;
    width: 100%;
}
.itemlist tbody tr:nth-of-type(1n + 1):nth-child(2n-1) td {
    background-color: rgb(26, 26, 27) !important;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}
.itemlist tbody tr.athing:nth-of-type(2n) + tr td {
    background-color: #141414 !important;
    padding-bottom: 6px;
    border-bottom: 1px solid #343536;
}
.itemlist tbody tr.athing:nth-of-type(2n-1) + tr td {
    background-color: rgb(26, 26, 27) !important;
    padding-bottom: 6px;
    border-bottom: 1px solid #343536;
}
/* Comments  */
 .comment {
    font-size: 1em;
    line-height: 1.2em;
}
.comment u, .comment u a, .comment u a:visited, .comment + p u, .comment + p u a, .comment + p u a:visited {
    text-decoration: none;
    color: #777;
}
.comment p, .comment + p {
    margin: .9em 0 0 0;
}
.comhead a[href*="user?id="] {
    font-weight: bold !important;
    color: #d35400 !important;
    font-size: 1.1em;
}
.comment-tree .comment, .fatitem .comment {
}
.comment-tree .comment {
}
tr.athing:nth-child(even) td .comment {
}
.fatitem {
}
.fatitem .comment {
}
.comment .c00 {
    color: rgb(129, 131, 132) !important;
}
.fatitem .title a:visited {
    color: #fff !important;
}
a.morelink {
    margin-bottom: 10px;
    display: block;
}
}
