css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(boards.4chan.org) 

{
body[bgcolor] {
background-image:none !important;
background-color:#F8F8F8 !important;
background-position: right bottom !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important
}



[type="submit"]:active,[type="button"]:active,[type="reset"]:active{
position: relative;
top: 1px;
left: 1px; 
}
[type="checkbox"]{
margin:3px !important;
}


/*Links to other boards*/
#navtop, #navbot { color: #000 !important; width: 100%; text-align: center; height: 12pt; font-size: 8pt !important; }
#navtop *, #navbot * { color: #CCC; font-size: 8pt; display: inline; }
#navtop a:hover, #navbot a:hover { background: #EEE; 
}
#hd, small, font[size="1"], font[size="2"], #option-button, .doubledash, img.rotating, td[align="right"], .rules, .pages td:first-child, .pages input { display: none !important; }


.reply, .replyhl {
background-image:none !important;
background-position: left bottom !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
-moz-border-radius:8px !important;

background-color:#C8C8C8 !important;
color: #000000 !important;
}

/* removes ugly borders on page overview [0,1,2...] */
table,td{border:none !important;}
td.rules{display:none !important;}


/* blurry thumbnails fix */
img[md5] {
height: auto !important;
width: auto !important;
}

/*Post Reply Box*/

div.postarea{position:fixed !important; 
left:900px !important;
top:465px !important;
width:500px !important;
opacity:0.00 !important;
}
 div.postarea:hover{opacity:1 !important;
}


/*Increase TextBox*/
 textarea{height:100px !important;
width:300px !important;
}

.replyhl{
background-color: #A3A3A3 !important;
}
blockquote {
color: #000000 !important;
}
.unkfunc{
color:#060 !important;
font-style: italic !important;
}

/* links */
a{text-decoration:none !important;}
a.quotejs:hover, a:hover{font-weight:100 !important;
text-decoration:underline !important;
}

.filesize {
color: #000000 !important;
}

/*POSTINGMODEREPLY*/
html > body > table > tbody > tr > td,
html > body > table > tbody > tr > th
{ opacity: 0 !important; }

/*Pages*/
.pages{
border:solid 0px #e4f6fa !important;
background:transparent !important;
}
span.filetitle, .replytitle { color: #000 !important; }
.pages { background: #ddd !important; border: 0 !important; }
.pages td { color: #ddd !important; font-size: 4pt; }
.pages td a, .pages td b { color: #000 !important; font-size: 8pt; }
.pages td b, .pages td a:hover { background-color: #CCC; }

pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select, input[type="submit"] { font-size: 9pt !important; 
background: #FFF !important; 
border: 1px solid #666 !important;
-moz-appearance: none; 
}


/*POSTINGMODE*/
.postarea{ 
color:#C8C8C8 !important;
opacity: 0.2 !important;
padding-left:0px !important;
}
iframe, .rotating {
width: 0px !important;
height:0px !important;
}
.title {
color: #C8C8C8 !important;
}
/*Watched Threads title*/
.postblock, .pages {
background-color: #C8C8C8 !important;
}
#watchedthreads,#twi_table_div,.reflinkpreview {
border:0px dotted #e4f6fa !important
}




/*4chan Extension Stuff*/
.hidethread,.unhidethread,.watchthread,.expandthread,.quickreply,.hidewatchedthreads,.refreshwatchedthreads,.restorewatchedthreads,* html .hidethread,.unhidethread,.watchthread,.expandthread,.quickreply,.hidewatchedthreads,.refreshwatchedthreads,.restorewatchedthreadsimg,img[src^="chrome://4chan/skin/buttons/"],img[alt="Locked"],img[alt="closed"] {
background-image:url('data:image/png;base64,') !important;
}
img[src^="chrome://4chan/skin/buttons/"],img[alt="Locked"],img[alt="closed"] {
height:0 !important;
margin:0 2px !important;
padding-top:16px !important;
width:16px !important;
background-color:transparent !important;
}
[src$="post_expand_plus.png"] {
background-position:-48px 0 !important
}
[src$="arrow_up.png"] {
background-position:-64px 0 !important
}
[src$="quote.png"] {
background-position:-80px 0 !important
}
[src$="stop.png"] {
background-position:-96px 0 !important
}
[src$="arrow_right.png"] {
background-position:-112px 0 !important
}
[src$="post_expand_minus.png"] {
background-position:-32px -16px !important
}
[src$="arrow_down.png"] {
background-position:-64px -16px !important
}
[src$="arrow_down2.png"] {
background-position:-80px -16px !important;
}
[src$="empty.png"] {
background-position:-96px -16px !important
}
img[alt="Locked"],img[alt="closed"] {
background-position:-112px -16px !important
}



/*Sage*/
.commentpostername a.linkmail[href="mailto:sage"]:before
{content: "¤SAGERU¤ " !important;
}
.commentpostername a.linkmail[href="mailto:sage"] {
color: #000000 !important;
}

.filetitle 
  {font-size: 80% !important;
 }

/*Logo*/
.logo {
color: #757575 !important;
}
/*Rules*/
.rules { display: none;
}

.span{
color:#C8C8C8 !important; }
DIV[class="4chan_ext_thread"] > BLOCKQUOTE {
}
hr{display:none !important; 
opacity: 0 !important; }

hr{height:0px !important; 
border:none !important; 
}

[class*="postername"] {
color:#444444 !important
}

[class*="postertrip"] {
color:#555555 !important
}

}
