css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nu.nl") {
  /* Grootte artikel veranderen */
  div[id="leadarticle"], div[id="articlebody"], .story, .comic_view { width: auto !important }
  div[id="leftcol"], div[id="leadarticle alg"], .leadarticle, #listblock, .subarticle { width: 490px !important }
  #phototextframe, .lead_txt { width: 224px !important }
  .sub_txt { width: 330px !important }
  .story { max-width: 450px !important }
  p, .summary { text-align: justify !important }
  div.sub_txt > p, div.lead_txt > p { text-align: left !important }

  /* Weghalen van het een en ander aan onzin */
  div[id="rightcol"],
  /*div[id="extrasbox"],*/     /* <-- Verwijder commentaar als je geen twitter link wilt */
  div[id="afc_rectangle_1"],
  div[id="leadarticle"] > center,
  div[id="feeds"],
  iframe[width="468"],
  iframe[width="120"],
  div[id="catstoryblock"] > center,
  div[id="padbox"][class="ltbluebox"],
  div.advertorial_red,
  div[id="advertorial_red_listblock"],
  div[id="adblock_v"],
  div[id="adblock_h"] { display: none !important }

  /* Midden gedeelte goed plaatsen */
  div[id="masthead"] { margin-top: 20px !important }

  /* Extra's box aanpassen */
  span.featbox { display: none !important }
  span.twitter { display: block !important }

  /* Zoekformulier juist plaatsen */
  input[id="zoekfield"], input[id="submit"] { top: 34px !important }

  /* Uitgegeven en Laatst gewijzigde datum op zelfde hoogte plaatsen */
  div[id="datestamp"] > table > tbody > tr { float: left !important; position: relative !important; }
  div[id="datestamp"] > table > tbody > tr + tr { float: right !important; position: relative !important; left: 120px !important}
}
