css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.google.com/search"), url-prefix("http://images.google.com/images"){
#guser {position: fixed !important;top: 0px !important;background-color:#fff !important;width:100% !important;left:0px !important;}
#guser + table {position: fixed !important;top: 20px !important;background-color: #fff !important;width:100% !important}
#guser + table ~ script + table {position: fixed !important;top:99px !important;left:0px !important;}
#guser + table ~ script + table + div, #guser + table ~ script + table + table + div, #guser + table ~ script + table + p, #guser + table ~ script + table + table + p {margin-top:130px !important;}
#navbar td:first-child, #nc, #nf, #nn, #np, #nl, #navbar .nr, body > center {display: none !important;}
#completeDiv, #completeTable {position:fixed !important;}


table[class="t bt"] {position: fixed !important; top: 95px !important;}
body {margin-top:125px !important; }

#gbar {position:fixed !important; top:25px !important; left: 165px !important; z-index:100 !important;} 
}
