/* ==UserStyle==
@name         CornerBlox
@version      20220612.16.02
@namespace    userstyles.world/user/darkpixlz
@description  Corners for Roblox that were left out
@author       darkpixlz
@license      No License
==/UserStyle== */

@-moz-document domain("roblox.com") {
div { 
border-radius:8px
}
.category-tabs {
    border-radius:8px
}
body.btr-embed .category-tabs {
    border-radius:8px
}
.dark-theme .rbx-tabs-vertical .category-tabs .menu-vertical {
    border-radius:8px
}
.dark-theme .stack .stack-list .stack-row {
    border-radius:8px;
}
.game-main-content.follow-button-enabled .favorite-follow-vote-share .voting-panel {
/*    border-radius:1px 5px 10px 20px; */
    border-radius:0px 0px 8px 8px;
    background:#2f2f2f;
}
.game-main-content.follow-button-enabled .favorite-follow-vote-share .voting-panel .users-vote .upvote {
        background:#2f2f2f;
        border-radius:8px 8px 0px 0px;
}
.tooltip-container {
            background:#2f2f2f;
        border-radius:8px;
    width:50px;
    
}
.game-main-content.follow-button-enabled .favorite-follow-vote-share .game-favorite-button-container .icon-label {
    display:none
        
}
.gotham-font .game-main-content.follow-button-enabled .favorite-follow-vote-share .game-favorite-button-container .favorite-button {
    width:50px
}
.dark-theme .scroller {
    border-radius:8px
}
.rbx-nav-sponsor img {
    border-radius:8px
}
.dark-theme .rbx-tabs-horizontal .nav-tabs {
    border-radius:8px
}
.content .page-content .form-group.birthday-container .rbx-select-group .rbx-select, .content .page-content .form-group.gender-container .gender-button {
    border-radius:8px
}
.dark-theme .chat-container .chat-main .chat-header {
    border-radius:8px 8px 0px 0px
}
.dark-theme .rbx-tabs-horizontal .rbx-tab .rbx-tab-heading {
    border-radius:8px
}
.dark-theme .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
    border-radius:0px 0px 0px 4px
}
.thumbnail-holder .thumbnail-span>img, .btr-bg-btn[data-color="none"], #AssetThumbnail[data-btr-bg="none"] .thumbnail-span {
    border-radius:8px
}
.dark-theme .table-striped>tbody>tr:nth-child(2n)>td, .dark-theme .table-striped>tbody>tr:nth-child(2n)>th {
    border-radius:8px
}
.amount icon-robux-container {
    border-radius:0px 0px 8px 8px !important;
}

/* wth this name is long */
.gotham-font.chat-container .btn-control-xs, .gotham-font .chat-container .btn-control-xs, .gotham-font.chat-container .chat-search-input, .gotham-font .chat-container .chat-search-input, .gotham-font .chat-main .btn-control-md, .gotham-font .create-chat-container .select-friends-btns .select-friends-cancel, .gotham-font .create-chat-container .select-friends-btns .select-friends-save, .gotham-font .details-btns-fixed button, .gotham-font .details-btns button, .gotham-font .details-container .details-input, .gotham-font .details-header-container .details-input, .gotham-font .dialog-container .dialog-input, .gotham-font .game-item-container .game-btn, .gotham-font .group-dialog .group-name-editor .group-name-input, .gotham-font .group-dialog .group-name-editor .group-name-save, .gotham-font .group-dialog .play-together-game-editor .group-name-input, .gotham-font .group-dialog .play-together-game-editor .group-name-save {
    border-radius:0px 0px 8px 8px;
}
.dark-theme .dialog-container .dialog-input-container .dialog-input {
    width:250px;
        right:50px !important
}
#settings-container .left-navigation, .dark-theme .menu-vertical {
    border-radius:8px
}
.catalog-container .search-container .input-group .search-input {
    border-radius:8px
}

