.sd_pulldown_header {
   padding: 10px;
   background-color: #FFF;
   font-family: Trebuchet MS;
}
center {
    display: none !important;
}

  .wall-broadcast-item {
      background-color: #FFF;
  }
  .comments > ul > li {
      background-color: #FFF;
  }
  ul.feed > li {
      border-bottom: 1px solid #FFF;
  }
  ul.feed .feed_item_attachments {
      background-color: #FFF;
  }
  .layout_announcement_list_announcements {
      border-left: 0px;
  }
  h3 {
      background-color: #FFF;
      border-top: 1px solid #3b5998;
  }
  
  .layout_wall_feed {
      border-left: 0px;
  }
  .arrowchat_tabpopup {
      background-color: #FFF;
      font-family: Trebuchet MS;
  }
  .arrowchat_powered_by {
      border-right: 1px solid #B2B2B2;
      border-left: 1px solid #B2B2B2;
      border-bottom: 1px solid #C9D0DA;
      background-color: #BFD9FF;
  }
  .arrowchat_userstabtitle {
      background-color: #3b5998;
      border-color: #CC6666;
  }
  .arrowchat_bar_button {
      background: #3b5998;
