      border-left: 0px;
  }
  .arrowchat_tabcontent {
      border-right: 1px solid #B2B2B2;
      border-left: 1px solid #B2B2B2;
      background-color: #B5CEF2;
  }
  .arrowchat_userlist {
      border-top: 1px solid #BFD9FF;
      border-bottom: 1px solid #BFD9FF;
  }
  #global_page_timeline-profile-index #global_content .tli {
      border: 1px solid #FFF;
      background-color: #FFF;
  }
  #global_page_timeline-profile-index #global_content .tl-block {
      background-color: #FFF;
      border-right: 0px;
      border-image: none;
      border-color: #FFF;
  }
  ul.feed > li:first-child {
      border-top: 0px;
  }
  .wall-menu-expand-link {
      border: 1px solid #FFF;
  }
  #global_page_wall-index-view .layout_middle {
      border-left: 0px;
  }
