css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(digg.com) {

#wrapper {
     padding-left: 0px; 
     margin-left: 105px;
     width: 500px !important; 
}

.news-body, .comment-tray, .comment{
     width: 500px !important;
}

.comment{
     padding-left: 110px !important;
}

[value="Check Spelling"] {
   display:none;
   visibility: hidden;
}

#creplyform{
   position: fixed !important;
   bottom: 250px !important;
   right: 40px !important;
   z-index:100;
}

#cforminfo + ol {

   position: fixed !important;
   bottom: 5px !important;
   right: 15px !important;
   width: 240px;
   z-index:1000;
}

#footer, .copyright{
   padding-right:355px;
   z-index:0;

}

.footer-contents{
   position:absolute;
   bottom:0px;
   right: 300px;
}

#contents{
   background-color: white;
}

#sub-nav{
   width:80%;
}

#container{
   background-color: white;
}

}
