css:
/*
name           Steam by Windoki 
version        1.1.0
description    Centered header. Panel "Recent games" in style of other panels. Another style for right panel in Steam profile.
author         Windoki Tarot
*/
@-moz-document domain("store.steampowered.com"),
domain("steamcommunity.com") {
    /* Header center */
    #global_header .content {
        position: static !important;

        display: flex;
        justify-content: center;
        align-items: center;

        width: auto !important;
        margin: 0 !important;
    }

    #global_header .logo {
        padding: 0 !important;
        margin: 0 !important;
    }

    #global_header .supernav_container {
        margin: 0 60px;
        position: static !important;
        left: 0 !important;
    }

    #global_header .supernav_container > a {
        padding-top: 0px !important;
        padding-bottom: 0px !important;
    }

    #global_actions {
        position: static !important;
        height: auto !important;
    }
}

@-moz-document domain("steamcommunity.com") {
    /* Right pannel */
    .responsive_status_info {
        text-align: center;
    }

    .profile_rightcol > div + div {
        margin: 40px 0 0 0 !important;
    }

    .responsive_count_link_area > div + div {
        margin: 40px 0 0 0 !important;
    }


    .profile_count_link {
        text-transform: uppercase;
        padding: 8px 12px 0px 12px;
        border-radius: 3px;
        font-size: 15px !important;
        transition: background-color 0.2s;
    }

    .profile_count_link:hover {
        background-color: rgba(0, 0, 0, 0.3);
    }

    .profile_count_link > a {
        display: flex;
        justify-content: space-between;
    }

    .count_link_label {
        text-decoration: underline rgba( 0, 0, 0, 0.0) !important;
    }

    .profile_count_link_total {
        color: #ebebeb !important;
        font-size: 15px !important;
    }

    /*   Lvl   */
    .profile_header_badgeinfo .persona_level {
    border-radius: 3px;
    padding: 6px 12px;
    display: flex;
    flex-direction: row;
    align-items: center;
    transition: background-color 0.3s;
    }
    
    .profile_header_badgeinfo .persona_level > div {
        margin-left: 12px;
    }
    
    .profile_header_badgeinfo .persona_level:hover {
    background-color: rgba(0,0,0,0.3);
    }
    
    /* Badge */
    .profile_header_badge {
        background-color: rgba( 0, 0, 0, 0.3) !important;
        transition: background-color 0.2s;
    }

    .profile_header_badge:hover {
        background-color: rgba( 0, 0, 0, 0.6) !important;
    }

    /* Proflie action buttons */
    .btn_profile_action {
        background-color: rgba( 0, 0, 0, 0.3) !important;
        border-radius: 3px !important;
        transition: all 0.3s !important;
    }

    .btn_profile_action > span {
        background-color: rgba( 0, 0, 0, 0.0) !important;
    }
    .btn_profile_action:hover {
        background-color: rgba( 0, 0, 0, 0.6) !important;
    }

    /* Friends list */
    .profile_topfriends .friendBlock {
        border-radius: 3px;
        transition: background-color 0.3s;
    }

    .profile_topfriends .friendBlock:hover {
        background-color: rgba(0, 0, 0, 0.3) !important;
    }
    
/*   FIX   */
    .profile_header {
        max-width: none !important;
        padding-right: 12px;
        padding-left: 18px;
    }
    .profile_header_content {
        padding-right: 0;
    }
    .profile_header_badgeinfo_badge_area{
        width: 288px !important;
    }
}

@-moz-document domain("steamcommunity.com") {
    /* Recent game */
    .recent_games {
        border-top-left-radius: 0;
        border-top-right-radius: 0;
        border-bottom-right-radius: 5px;
        border-bottom-left-radius: 5px;
        padding: 11px 10px;
        margin-bottom: 12px;
        background-color: rgba(0, 0, 0, 0.3);
    }

    .recent_games > div {
        border-radius: 5px;
    }

    .profile_recentgame_header {
        border-top-left-radius: 5px !important;
        border-top-right-radius: 5px !important;
        border-bottom-right-radius: 0 !important;
        border-bottom-left-radius: 0 !important;
        background: var(--color-showcase-header);
        background: linear-gradient(90deg, var(--gradient-showcase-header-left) 0%, var(--color-showcase-header) 90%);
        padding: 5px 10px 5px 10px !important;
        color: #ffffff;
        margin: 0 !important;
    }

    .profile_recentgame_header h2 {
        font-size: 16px !important;
    }


    .recent_game {
        margin-bottom: 0 !important;
    }

    .recent_game + .recent_game {
        margin-top: 20px;
    }
}

@-moz-document domain("steamcommunity.com") {
    /*     Showcases  */
    /* Remove black border from pictures  */
    .screenshot_showcase_screenshot {
        border: 1px solid #0000 !important;
        transition: border 0.3s;
    }
    .screenshot_showcase_screenshot:hover {
        border: 1px solid #97C0E3 !important;
    }
}
@-moz-document domain("steamcommunity.com") {
    /* Comments */
    .profile_comment_area .commentthread_comment {
        transition: background-color 0.3s;
        border-radius: 3px;
    }

    .profile_comment_area .commentthread_comment:not(.hidden_post):hover {
        background: rgba(0, 0, 0, 0.3) !important;
    }
}
