@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com")
{
  

  a, .fw_reply_author, .post_share_count.fl_l, .post_like_count.fl_l, .post_like_link.fl_l, #pv_like_link, #pv_like_count, .header_top.clear_fix, ._reply_lnk, .pad_msg_date a, .left_box.attention h4, .summary.fl_l, .summary_tab3, .fans_idol_unsub a, #profile_videos .p_header_bottom, h4, .medadd_h.medadd_h_link.inl_bl, a.wall_reply_greeting, .summary, .group_row_labeled a, .post_like, .post_share, .audio_feed_post .audio_feed_text, .tb_title, .page_counter .count, .page_counter .label, .video_sort_dd_wrap .video_sort_dd, .mv_add_button.flat_button.secondary, .mv_delete_button.flat_button.secondary, .mv_edit_button.flat_button.secondary, .mv_share_button.flat_button.secondary, .video_privacy_edit a, .gift_name, .gift_actions a, .pv_like_link, .feedback_header .author, .feedback_header .group_link, .feedback_header .mem_link, header_side_link, .header_side_link a, .ui_actions_menu_item, .fans_idol_lnk, .wide_column .topics_module .topic_title, .topics_module .topic_inner_link
  {
    color: #0091FF;
  }

  .scroll_fix
  {
    background: url("http://savepic.net/9510743.png") no-repeat fixed 0% 0%;
    background-size: cover;
  }

  #side_bar ol li .left_row:hover
  {
    background: #000;
  }

  #page_header .right, #page_header .left, #pageHeader .header_back, #page_header, #pageHeader
  {
    background: url("http://savepic.net/8375958.png") no-repeat !important;
    width: 100%;
  }

  #mv_more, .pad_msg_lnk, #pv_wide .wall_module .author, .wall_module .mv_comm .author, .wk_likes_liker_lnk
  {
    color: blue;
  }

  .top_home_logo
  {
    visibility: hidden;
  }

  #side_bar .left_label, box_title, .page_actions_dd_label, .page_actions_header_inner
  {
    color: #fff;
  }

  .post_author .author, .wall_module .author, .wall_module .copy_author, .ts_contact_title
  {
    color: #4686bd;
  }

  .button_gray button.hover, .button_gray button, .flat_button.secondary.hover, .flat_button.secondary, .flat_button, #mv_like_wrap
  {
    color: #fff;
    background-color: #35A2E7;
  }

  .online::after, .nim-dialog.nim-dialog_selected:not(.nim-dialog_deleted) .nim-dialog--photo .online::after, .chat_tab_imgcont.online::after
  {
    background-color: #0091FF;
    border: 2px solid #fff;
  }

  .top_nav_btn:hover, .top_audio_player:hover, .top_nav_link:hover, .button_gray button.hover, .button_gray button:hover, .flat_button.secondary.hover, .flat_button.secondary:hover, .flat_button:hover
  {
    background-color: #277BB0;
  }

  .box_title_wrap.box_grey, .box_title_wrap
  {
    background-color: #35A2E7;
  }

  .ui_tab_sel, .ui_tab_sel:hover, .ui_tabs .ui_tab_sel, .ui_tabs .ui_tab_sel:hover, .ui_tabs_box .ui_tab_sel, .ui_tabs_box .ui_tab_sel:hover
  {
    border-bottom: 2px solid #000;
  }
}
