css:
@-moz-document url("https://omni.se/"), domain("omni.se") {
body, .app-bg, .group, .article, .component, .resource, .article-ago, .article--compact .resource--text:after, .btn--light {
    background: #212121 !important;
    background-color: #212121 !important;
    color: white !important;
}
.article--sponsored .resource--title {
    color: #d0d3a9 !important;
}
.group, .box-heading {
    border-bottom: 8px solid #212121;
}
.component--topicBox--storyHeading > a {
    background: #212121 !important;
}
.header, .menu {
    background-color: #111111 !important;
}
.topnav a, .search, .search input {
    background: #212121 !important;
    background-color: #212121 !important;
    color: white !important;
}
.topnav, .article--sponsored::before {
    background: none;
}
.usermix-category-option-label {
    background: #212121;
}
.resource--title--container {
    background-color: #212121;
}
.main-narrow--side {
    background-color: #212121;
}
.feed--partial {
    background-color: #212121;
}
.starbox-star {
    background-color: #212121;
}
.resource--link-title {
    color: #ffffff;
}
.resource--labeledicon {
    background-color: #212121;
}
.salesposter {
    background: #5c8e81;
}
.article.article--full.article-focused.article--expanded[style="--primary:rgba(222,94,58,1);"], .article-resource_column--licensed, .app-feedback, .resource--evergreen_article_link--premium {
    display: none;
}
}
