css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("coma.gameforge.de") {


/*Menu makes it positioned on the left side*/
.qmmc {
border-top: 15px solid #6B7898 !important;
padding-top: 0px !important;
padding-bottom: 3px !important;
background: none !important;
position: absolute !important;
left: 10px !important;
width: 120px !important;
top: 22px !important;
font-weight: bold;
}
/*makes the hidden menu items visible*/
.qmmc ul {
left: 0px !important;
position: relative !important;
}

/*removes backgrounds*/
.qmmc *{
background: none !important;
border: 0px !important;
padding-left: 3px !important;
}
/*Makes sure all items are on the same line*/
.qmmc * *{
padding-left: 0px !important;
margin: 0px !important;
}
/*Hides all links in menu [See next item]*/
.qmmc a {
display: none !important;

}

/*Makes all links with a href attribute visible again, hiding the "menu heads"*/
.qmmc a[href*="php"] {
display: block !important;
}

/*underline on hover*/
.qmmc a[href*="php"]:hover {
display: block !important;
text-decoration: underline !important;
}


/*makes deeviding lines disapear*/
.line {
height: 0px !important;
}

/*Places the main content ~10px next to menu instead of centred (so menu and content wont overlap) also changes width*/
table[align="center"][width="990"]:first-child{
position: absolute !important;
left: 140px !important;
width: 80% !important;

}

/*Makes content be 100% of its container, instead of a fixed size*/
table table[align="center"][width="990"]:last-child{
width: 100% !important;
}

/*Hide all deviders*/
.block {
display: none !important;
}

/*Adds skin made by next to copyright, if you want, you can delete it*/
td.text[align="center"]:nth-child(1):after{
content: " ~ Skin Made By Werring ~";
}

/*Removes skin made by from places it shouldn't be*/
form td.text[align="center"]:nth-child(1):after{
content: "" !important;
}


}
