css:
@-moz-document domain(youtube.com) {
ytd-watch-flexy[theater]:not([fullscreen]) #player-wide-container video {
    top: 0 !important;
    left: 0 !important;
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-color: var(--yt-spec-general-background-a)!important;
    object-fit: contain !important;
}

ytd-watch-flexy[theater]:not([fullscreen]) #player-wide-container {
    position: relative !important;
    width: 100% !important;
    height: calc(100vh - var(--ytd-toolbar-height)) !important;
    max-height: unset !important;
}

ytd-watch-flexy[theater]:not([fullscreen]) #player-wide-container .html5-video-player {
    z-index: 9999 !important;
}

ytd-watch-flexy[theater]:not([fullscreen]) #columns {
    background-color: var(--yt-spec-general-background-a)!important;
    position: relative !important;
    z-index: 10000 !important;
}
}
