css:
@-moz-document domain('twitter.com'), url-prefix('http://twitter.com') {
	#container{width:990px !important;margin-top:4px!important;}
        ol.statuses span.status-body{width:690px!important}
        #side_base {width:199px!important;}

.bar h3 label{
font-weight: bolder!important;
font-size: 12px!important;
line-height: 1.1!important;
width: 50%!important;
margin-bottom: 5px!important;
}

.content-bubble-arrow{display:none !important;}

.top-navigation li a{
padding-top: 0.2em!important;
padding-bottom: 0.2em!important;
font-size: 0.8em!important;
}
#header{ margin-bottom:5px!important; }

#logo img{
width: 80px!important;height: 18px!important;
margin-top: 1px!important;
}

.status-update-form textarea{
height: 2.2em!important;
font-size: 1.0em!important;
width:600px!important;
}

#update_notifications{
min-height: 15px!important;
width: 600px!important;
margin-top: 5px!important;
}

#content div.section{
padding-top: 0px!important;
}

#side .promotion{display:none!important;}

#side div#profile.section{margin-bottom: 8pt!important;}


body.timeline #content h1{
font-size: 13px!important;
font-weight: bolder!important;
}

ol.statuses li{
padding-top: 5px!important;
padding-bottom: 5px!important;
}

#timeline_heading h1{margin-bottom: -0px!important;}

ol.statuses .thumb img{
width: 40px!important;
height: 40px!important;
}


ol.statuses .thumb{
width: 40px!important;
height: 40px!important;
}

ol.statuses span.status-body{
min-height: 40px!important;
}


ol.statuses li.last-on-page, ol.statuses li.last-on-refresh{
border-bottom-width: 4px!important;
margin-bottom: 5px!important;

}


body#show #content{

width: 97%!important;
margin:0 auto!important;

}

body#show #footer{
width: 100%!important;
margin:10px auto!important;
}


.status-btn input.round-btn{
width: 110px!important;
height: 30px!important;
font-size: 12px!important;
margin-left: 0px!important;
border: 1px solid gray!important;
}

.status-btn{
padding-top: 0px!important;
}

.actions-hover li{
bottom:-10px!important;
background-color:transparent!important;

}



}
