css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yandex.ru")
{

  #bodyContainer,searchbox
  {
    background: transparent !important;
  }

  *
  {
    -moz-border-radius: 15px 0px 15px 0px !important;
  }

  tr,span,iframe,tbody,p,ol,li,ul,hr,center,td:not([style*="color"]), div:not([style*="color"])
  {
    background-color: transparent !important;
    color: #72addf !important;
  }

  a:not([style*="color"]):not([style*="background"]), th, h1, h2, h3, h4
  {
    background-color: transparent !important;
  }

  table
  {
    background-color: transparent !important;
    border-color: #000000 !important;
    border-radius: 6px !important;
    color: transparent !important;
  }

  body
  {
    background: url(https://srv2.imgonline.com.ua/result_img/imgonline-com-ua-Mirror-GvOB9eq6w7.jpg) #000 no-repeat !important;
    background-size: 100%!important;
    background-repeat: no-repeat!important;
    background-attachment: fixed!important;
    font-family: "constantia"!important;
  }

  FORM *:not([name="q"]):not([type="submit"]):not([type="button"]):not(img):not([title="Firefox Start"])
  {
    background: transparent !important;
  }

  a:link, .link, a font[color], #results-bar p, .suggestion-title
  {
    color: #000000 !important;
  }

  a:visited
  {
    color: #000000 !important;
  }

  a:hover, a span:hover
  {
    color: #000000 !important;
    text-shadow: #000000 0px 0px 2px;
  }
}
