css:
@-moz-document url-prefix("https://stackoverflow.com"),
url-prefix("https://www.bejson.com"),
url-prefix("https://www.baeldung.com"),
url-prefix("https://mvnrepository.com") {

    /* +++++++++++++++++ stackoverflow ++++++++++++++++ */
    #content {
        width: 100%;
        max-width: none;
    }

    #left-sidebar,
    #sidebar {
        display: none;
    }

    .js-zone-container,
    .js-consent-banner,
    .js-sidebar-zone {
        display: none;
    }

    #mainbar,
    .mainbar {
        width: 100%;
    }

    /* +++++++++++  bejson +++++++++++++++++++++ */
    div.html-footer2,
    div.xf2-gg-left,
    #aliLeftBox,
    #feee {
        display: none;
    }

    div.container.t-small-margin>div:nth-child(5),
    div.container.t-small-margin>ul {
        display: none;
    }

    div.xf-window {
        display: none !important;
    }

    div.container.t-small-margin>div.t-big-margin {
        display: none;
    }

    /* ++++++++++++ baeldung +++++++++++++ */
    #content>#main {
        width: 100% !important;
    }

    #z1655297175893,
    #sidebar1,
    #fs-sticky-footer {
        display: none;
    }

    div.widget_text.after-post-banner-widget,
    div.code-block,
    div.after-post-widgets {
        display: none;
    }

    .after-post-banner-widget {
        display: none;
    }

    /* ++++++++++++ baeldung +++++++++++++ */
    #content>#main {
        width: 100% !important;
    }

    #z1655297175893,
    #sidebar1,
    #fs-sticky-footer {
        display: none;
    }

    div.widget_text.after-post-banner-widget,
    div.code-block,
    div.after-post-widgets {
        display: none;
    }

    .after-post-banner-widget {
        display: none;
    }

    /* ++++++++++++ maven +++++++++++++ */
    body>div>main>div.right {
        display: none;
    }

    body>div.page>div.sidebar {
        display: none;
    }

    body {
        font-size: 16px;
    }

    .page .center .content {
        padding: 2em;
        height: 100vh;
    }

    .page .center {
        grid-template-columns: none;
    }

    body>div>main>div.content>ins::after {
        display: none;
    }

    body>footer {
        display: none;
    }
}

@-moz-document url-prefix("https://angular.cn"),
url-prefix("https://angular.io"),
url-prefix("https://material.angular") {

    /* angular cookies*/
    app-cookie-popup,
    aio-cookies-popup {
        display: none;
    }
}
