css:
@-moz-document domain("amazon.com.au")
{
  
  .feed-carousel-card .product-image
  {
    width: 80% !important;
  }

  #landingImage
  {
    width: 80% !important;
  }

  .a-dynamic-image.a-stretch-vertical
  {
    height: auto !important;
  }

  .feed-carousel-card
  {
    padding: 5px !important;
    border-right: 1px solid #f5f5f5 !important;
    margin: 10px !important;
  }

  .a-carousel-card
  {
    padding: 15px !important;
  }

  #nav-upnav, .rhf-frame, #rhf-border
  {
    display: none !important;
  }

  body
  {
    background: url(https://c4.wallpaperflare.com/wallpaper/216/652/941/spring-trees-drawing-wallpaper-preview.jpg) !important;
    background-size: 100% !important;
    background-attachment: fixed !important;
  }

  .navSwmHoliday
  {
    background: none !important;
  }

  #nav-belt, #nav-main, #navbar
  {
    background: none !important;
    /* background:rgba(255,255,255,0.5) !important; */;
  }

  #navbar
  {
    background: rgba(0,0,0,0.5) !important;
  }

  .a-carousel-center
  {
    display: none !important;
  }

  .bestsellers, .desktop-row, .sidekick
  {
    width: 100%;
    background: rgba(255,255,255,0.7) !important;
  }

  .a-fixed-right-grid-inner, .shogun-widget
  {
    background: none !important;
  }

  #main-content-top
  {
    background: transparent !important;
  }

  #rhf-container
  {
    background: transparent !important;
  }

  #navFooter.navLeftFooter
  {
    background-color: rgba(35,47,62,0.8) !important;
  }

  #navFooter.navLeftFooter .navFooterDescLine
  {
    background-color: rgba(35,47,62,0) !important;
  }

  #rhf .rhf-fresh-header
  {
    color: #fff !important;
  }

  #searchTemplate, #rightContainerATF, #divsinglecolumnminwidth
  {
    background: rgba(255,255,255,0.8) !important;
  }

  #pageContent
  {
    background: rgba(255,255,255,0.5) !important;
  }

  #a-page
  {
    background: rgba(255,255,255,0.5) !important;
  }

  #dp
  {
    background: rgba(255,255,255,0.7) !important;
  }
}
