    display: none;
  }
  #juejin > div.view-container > main > div > div.sidebar.sidebar > ul{
    display: none;
  }
  #juejin > div.sidebar-block.app-download-sidebar-block.shadow{
      display: none;
  }
  #juejin > div.view-container > main > div > div.sidebar.sidebar > div.sidebar-block.app-download-sidebar-block.shadow{
     display: none;
  }
  #juejin > div.view-container.container > main > div > div > div > aside{
