css:
@-moz-document url-prefix("https://www.bilibili.tv")
  {
    /* i really want this to be global */
    #bilibiliPlayer > div > div > div.player-mobile-box.player-mobile-autoplay > div.player-mobile-display.player-mobile-control-bar-hide > div.player-mobile-subtitle > div > div > div:nth-child(1) > span, .player-mobile .subtitle-item-text {
      background: rgba(51, 51, 51, 0.6) !important;
    }
    #bilibiliPlayer > div > div > div.player-mobile-box.player-mobile-autoplay > div.player-mobile-display.player-mobile-control-bar-hide > div.player-mobile-subtitle, .player-mobile-subtitle{
      color: rgb(255, 255, 255) !important;
    }
 }
