css:
html, body { 
width: 33000px !important; 
} 
/* About 33000 pixels is the empirically determined width that the opera web browser window does not go beyond; even if a higher value is specified. 
It may not be a coincidence that this number is near 2^15 = 32768 */

body p, body h1, body h2, body h3, body h4, body h5, body h6, body div, body ul, body ol, body dl, body menu, body dir, body pre, body hr, body blockquote, body address, body center, body noframes, body isindex, body fieldset, body table, body table, body form, body caption, body tr, body tbody, body td, body th
{
display: inline !important;
text-align: left !important;
line-height: normal !important;
letter-spacing: normal !important;
padding: 0 !important;
margin: 0 !important;
border: 0 !important;
width: 100% !important;
clear: none !important;
float: none !important;
position: static !important;
}

body img, body li, body dd, body dt, body input, body a, body span, body strong, body em, body b, body i, body td
/* Do we need other inline tags here? Maybe only if they are styled to be blocks? */
{
display: inline !important;
text-align: left !important;
line-height: normal !important;
letter-spacing: normal !important;
padding: 0 !important;
margin: 0 !important;
border: 0 !important;
clear: none !important;
float: none !important;
position: static !important;
}

/* Since images are inherently non-inline. */
img { 
max-width: 12px !important;
max-height: 12px !important;
}

body br
{
display: none !important;
}

/* This spacing is a way to identify sections independant of vertical space */
h1 + h1, h1 + h2, h1 + h3, h1 + h4, h1 + h5, h1 + h6, h1 + p, h1 + div, h1 + ul,
h2 + h1, h2 + h2, h2 + h3, h2 + h4, h2 + h5, h2 + h6, h2 + p, h2 + div, h2 + ul,
h3 + h1, h3 + h2, h3 + h3, h3 + h4, h3 + h5, h3 + h6, h3 + p, h3 + div, h3 + ul,
h4 + h1, h4 + h2, h4 + h3, h4 + h4, h4 + h5, h4 + h6, h4 + p, h4 + div, h4 + ul,
h5 + h1, h5 + h2, h5 + h3, h5 + h4, h5 + h5, h5 + h6, h5 + p, h5 + div, h5 + ul,
h6 + h1, h6 + h2, h6 + h3, h6 + h4, h6 + h5, h6 + h6, h6 + p, h6 + div, h6 + ul,
p + h1, p + h2, p + h3, p + h4, p + h5, p + h6, p + p, p + div, p + ul,
div + h1, div + h2, div + h3, div + h4, div + h5, div + h6, div + div, div + ul,
ul + h1, ul + h2, ul + h3, ul + h4, ul + h5, ul + h6, ul + div, ul + ul
{ 
margin-left: 2em !important; 
}

h1 + li, h1 + h2, h1 + h3, h1 + h4, h1 + h5, h1 + h6, h1 + p, h1 + div, h1 + ul, h1 + li,
h2 + li,
h3 + li,
h4 + li,
h5 + li,
h6 + li,
p + li,
div + li,
ul + li,
li + li
{ 
margin-left: 1em !important; 
}
