css:
/*
	==== MyAnimeList.net - Alternative Layout ====
	Based on code written by Bastvera.
*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*"){*{/*$*/}
	#menu {
		width:					1110px !important;
	}
	h1 {
		width:					952px !important;
	}
	#content {
		width:					970px !important;
	}

	/* Content moved to right by side menu size (132) */
	#contentWrapper {
		padding:				1px 0px 0px 140px !important;
	}

	/* Menu Position */
	#menu_left{
		float:					none !important;
		width:					0px !important;
		position:				relative !important;
			top:				13px !important;
	}

	/* Menu Header Style */
	#menu_left #nav>li>a {
		height:					17px !important;
		width:					130px !important;
		padding:				3px 0px 1px 0px !important;
		/* Anti blink */
		background:				none repeat scroll 0 0 #458 !important;
	}

	#menu_left #nav>li {
		margin:					5px 0px 0px 0px !important;
	}

	/*Menu Items Style List*/
	#menu_left #nav>li>ul {
		display:				block !important;
		width:					130px !important;
	}

	#menu_left #nav li ul li {
		background:				none !important;
	}

	#menu_left #nav li ul li:hover {
		margin-bottom:			0 !important;
	}

	#menu_left #nav ul.wider span.edit a {
		height:					18px !important;
		line-height:			18px !important;
		margin:					1px 2px 0 0 !important;
		font-size:				10px !important;
	}

	/* Menu Positions */
	#menu_left>#nav * {
		float:					none !important;
	}

	#menu_left>#nav li *  {
		position:				relative !important;
	}

	#menu_left>#nav>li .edit {
		position:				absolute !important;
			top:				-1px !important;
			left:				103px !important;
		z-index:				1000 !important;
	}

	#menu_left>#nav>li .new {

		position:				absolute !important;
			top:				-4px !important;
			left:				107px !important;
		z-index:				1000 !important;
	}

	/*Menu Items Style List Items*/
	#menu_left #nav li ul li {
		height:					20px !important;
		width:					130px !important;
	}

	/*Menu Items Style Links (Edit)*/
	#menu_left #nav>li>ul a {
		height:					17px !important;
		padding:				2px 2px 1px 0px !important;
	}
}
